import React, { useState } from 'react';
import axios from 'axios';
import './LoginPage.css';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

function LoginPage() {
  const [loginMode, setLoginMode] = useState('admin');
  const [formType, setFormType] = useState('login'); 
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  
  const navigate = useNavigate();

  const clearFields = () => {
    setEmail('');
    setPassword('');
    setError('');
    setMessage('');
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('http://localhost:5000/api/login', { email, password });
      const token = res.data.token;
      localStorage.setItem('token', token);
      
      const decodedToken = jwtDecode(token);
      
      if (decodedToken.role === 'admin') {
        // Admin, kendi admin paneline gider.
        navigate('/homepage', { replace: true });
      } else {
        // ===> DEĞİŞİKLİK BURADA <===
        // Normal kullanıcı artık yeni kullanıcı paneline gider.
        navigate('/homepage/', { replace: true });
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Giriş işlemi başarısız');
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/register', {
        email,
        password
      });
      setMessage(res.data.message);
      clearFields();
      setTimeout(() => {
        setFormType('login');
        setMessage(''); 
      }, 2000); 
    } catch (err) {
      setError(err.response?.data?.message || 'Kayıt hatası');
    }
  };

  // JSX kısmında bir değişiklik yok.
  return (
    <div className="login-container">
      <div className="login-wrapper">
        
        <div className="role-tabs">
          <button 
            className={`tab-button ${loginMode === 'admin' ? 'active' : ''}`}
            onClick={() => setLoginMode('admin')}
          >
            Yönetici Girişi
          </button>
          <button 
            className={`tab-button ${loginMode === 'user' ? 'active' : ''}`}
            onClick={() => setLoginMode('user')}
          >
            Kullanıcı Girişi
          </button>
        </div>

        <form className="login-form" onSubmit={formType === 'login' ? handleLogin : handleRegister}>
          <h2>{formType === 'login' ? (loginMode === 'admin' ? 'Yönetici Girişi' : 'Kullanıcı Girişi') : 'Kaydol'}</h2>

          <input
            type="email"
            placeholder="E-posta Adresi"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Şifre"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          
          <button type="submit">
            {formType === 'login' ? 'Giriş Yap' : 'Kaydol'}
          </button>

          {error && <p className="error">{error}</p>}
          {message && <p className="success">{message}</p>}

          <div className="form-switch">
            <button
              type="button"
              onClick={() => {
                setFormType(formType === 'login' ? 'register' : 'login');
                clearFields();
              }}
            >
              {formType === 'login' ? 'Kaydol' : 'Giriş Yap'}
            </button>
            <button type="button" onClick={() => navigate('/forgot-password')}>
              Şifremi Unuttum
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
