import React, { useState } from 'react';
import axios from 'axios';
import './LoginPage.css'; // Aynı stil dosyasını kullanıyoruz

function ForgotPasswordPage() {
  const [step, setStep] = useState(1); // 1: e-posta gir, 2: kodu ve yeni şifreyi gir, 3: başarı mesajı
  const [email, setEmail] = useState('');
  const [code, setCode] = useState(''); // Yeni state: güvenlik kodu için
  const [newPassword, setNewPassword] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Adım 1: Kullanıcı e-postasını girip kod istediğinde çalışır
  const handleRequestCode = async () => {
    setIsLoading(true);
    setError('');
    try {
      // YENİ BACKEND ENDPOINT'İ
      const res = await axios.post('http://localhost:5000/api/request-password-reset', { email });
      setMessage(res.data.message);
      setStep(2); // Başarılı olursa 2. adıma geç
    } catch (err) {
      setError(err.response?.data?.message || 'Hata oluştu');
    } finally {
      setIsLoading(false);
    }
  };

  // Adım 2: Kullanıcı kodu ve yeni şifreyi girip gönderdiğinde çalışır
  const handleResetPassword = async () => {
    setIsLoading(true);
    setError('');
    try {
      // YENİ BACKEND ENDPOINT'İ
      const res = await axios.post('http://localhost:5000/api/verify-and-reset-password', {
        email,
        code,
        newPassword
      });
      setMessage(res.data.message);
      setError('');
      setStep(3); // Başarılı olursa 3. adıma geç (sonuç ekranı)
    } catch (err) {
      setError(err.response?.data?.message || 'Hata oluştu');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={(e) => e.preventDefault()}>
        <h2>Şifre Sıfırlama</h2>

        {/* Adım 1: E-posta isteme ekranı */}
        {step === 1 && (
          <>
            <p>Şifrenizi sıfırlamak için kayıtlı e-posta adresinizi girin.</p>
            <input
              type="email"
              placeholder="Kayıtlı e-posta"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <button type="button" onClick={handleRequestCode} disabled={isLoading}>
              {isLoading ? 'Gönderiliyor...' : 'Sıfırlama Kodu Gönder'}
            </button>
          </>
        )}

        {/* Adım 2: Kod ve yeni şifre isteme ekranı */}
        {step === 2 && (
          <>
            {message && <p className="success">{message}</p>}
            <p>E-postanıza gönderilen 6 haneli kodu giriniz.</p>
            <input
              type="text"
              placeholder="Güvenlik Kodu"
              value={code}
              onChange={(e) => setCode(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Yeni Şifre"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              required
            />
            <button type="button" onClick={handleResetPassword} disabled={isLoading}>
              {isLoading ? 'Güncelleniyor...' : 'Şifreyi Güncelle'}
            </button>
          </>
        )}

        {/* Adım 3: Başarı ekranı */}
        {step === 3 && (
          <>
            <p className="success">{message}</p>
            <a href="/login">Giriş Sayfasına Dön</a>
          </>
        )}

        {error && <p className="error">{error}</p>}
      </form>
    </div>
  );
}

export default ForgotPasswordPage;
