import React, { useState } from 'react';
import axios from 'axios';

function ForgotPasswordPage() {
  const [step, setStep] = useState(1);
  const [email, setEmail] = useState('');
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const getSecurityQuestion = async () => {
    try {
      const res = await axios.post('http://localhost:5000/api/forgot-password-question', { email });
      setQuestion(res.data.question);
      setStep(2);
    } catch (err) {
      setError(err.response?.data?.message || 'Hata oluştu');
    }
  };

  const resetPassword = async () => {
    try {
      const res = await axios.post('http://localhost:5000/api/reset-password', {
        email,
        answer,
        newPassword
      });
      setMessage(res.data.message);
      setError('');
      setStep(3);
    } catch (err) {
      setError(err.response?.data?.message || 'Hata oluştu');
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={(e) => e.preventDefault()}>
        <h2>Şifre Sıfırlama</h2>

        {step === 1 && (
          <>
            <input
              type="email"
              placeholder="Kayıtlı e-posta"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <button type="button" onClick={getSecurityQuestion}>Güvenlik Sorusu Göster</button>
          </>
        )}

        {step === 2 && (
          <>
            <p><strong>Soru:</strong> {question}</p>
            <input
              type="text"
              placeholder="Cevabınız"
              value={answer}
              onChange={(e) => setAnswer(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Yeni Şifre"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              required
            />
            <button type="button" onClick={resetPassword}>Şifreyi Güncelle</button>
          </>
        )}

        {step === 3 && (
          <>
            <p className="success">{message}</p>
            <a href="/">Giriş Sayfasına Dön</a>
          </>
        )}

        {error && <p className="error">{error}</p>}
      </form>
    </div>
  );
}

export default ForgotPasswordPage;
