import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { socket } from './socket';
import { useNavigate } from 'react-router-dom';
import './UserMessagesButton.css';

function UserMessagesButton() {
  const [unreadCount, setUnreadCount] = useState(0);
  const navigate = useNavigate();

  // OkunmamÄ±ÅŸ mesaj sayÄ±sÄ±nÄ± getir
  const fetchUnreadCount = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;

      const response = await axios.get('http://localhost:5000/api/user-notifications/unread-count', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      setUnreadCount(response.data.unreadCount);
    } catch (error) {
      console.error('OkunmamÄ±ÅŸ mesaj sayÄ±sÄ± alÄ±namadÄ±:', error);
      // Hata durumunda sayÄ±yÄ± sÄ±fÄ±rlama, kullanÄ±cÄ± deneyimini bozmamak iÃ§in
    }
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;

    // Ä°lk yÃ¼klemede sayÄ±yÄ± getir
    fetchUnreadCount();

    // Socket baÄŸlantÄ±sÄ±nÄ± kur
    if (!socket.connected) {
      socket.connect();
    }

    // Yeni mesaj geldiÄŸinde sayÄ±yÄ± artÄ±r
    const handleNewUnreadMessage = (data) => {
      console.log('ðŸ“© KullanÄ±cÄ±ya yeni mesaj bildirimi:', data);
      setUnreadCount(prev => prev + 1);
    };

    // Bildirimler temizlendiÄŸinde sayÄ±yÄ± sÄ±fÄ±rla
    const handleNotificationsReset = () => {
      console.log('ðŸ“­ KullanÄ±cÄ± bildirimleri sÄ±fÄ±rlandÄ±');
      setUnreadCount(0);
    };

    // Socket event'lerini dinle
    socket.on('user_new_unread_message', handleNewUnreadMessage);
    socket.on('user_notifications_were_reset', handleNotificationsReset);

    return () => {
      socket.off('user_new_unread_message', handleNewUnreadMessage);
      socket.off('user_notifications_were_reset', handleNotificationsReset);
    };
  }, []);

  const handleMessagesClick = () => {
    navigate('/messages');
  };

  return (
    <button className="user-messages-button" onClick={handleMessagesClick} title="MesajlarÄ±m">
      <span className="messages-icon">ðŸ’¬</span>
      <span className="messages-text">Mesajlar</span>
      {unreadCount > 0 && (
        <span className="unread-badge">{unreadCount > 99 ? '99+' : unreadCount}</span>
      )}
    </button>
  );
}

export default UserMessagesButton;
