import React, { useState } from 'react';
import './MarketValuePage.css'; // CSS dosyanızın yolunu kontrol edin.

function MarketValuePage() {
    const [filters, setFilters] = useState({
        brand: '',
        model: '',
        series: '',
        trimPackage: '',
        yearMin: '',
        yearMax: '',
        kmMax: '',
        fuelTypes: [], // Değerler: "1" (Benzin), "2" (Dizel), "3" (LPG)
        gearTypes: [], // Değerler: "1" (Manuel), "2" (Otomatik)
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFilters(prev => ({ ...prev, [name]: value }));
    };

    const handleCheckboxChange = (e) => {
        const { name, value, checked } = e.target;
        setFilters(prev => {
            const currentValues = prev[name];
            if (checked) {
                return { ...prev, [name]: [...currentValues, value] };
            } else {
                return { ...prev, [name]: currentValues.filter(item => item !== value) };
            }
        });
    };

    // Sahibinden.com için gerçek URL yapısını oluşturan nihai fonksiyon
    const redirectToSahibinden = () => {
        const baseDomain = 'https://www.sahibinden.com';
        
        // URL yolunu (path) oluşturmak için parçaları bir dizede toplayacağız.
        const pathParts = ['otomobil'];

        // Boşlukları '-' ile değiştirip küçük harfe çeviren yardımcı fonksiyon
        const formatForUrl = (text) => text.trim().toLowerCase().replace(/\s+/g, '-');

        // 1. ADIM: Araç hiyerarşisini tek bir path parçası olarak birleştir.
        const vehicleHierarchyParts = [];
        if (filters.brand) vehicleHierarchyParts.push(formatForUrl(filters.brand));
        if (filters.model) vehicleHierarchyParts.push(formatForUrl(filters.model));
        if (filters.series) vehicleHierarchyParts.push(formatForUrl(filters.series));
        if (filters.trimPackage) vehicleHierarchyParts.push(formatForUrl(filters.trimPackage));
        
        if (vehicleHierarchyParts.length > 0) {
            pathParts.push(vehicleHierarchyParts.join('-'));
        }

        // 2. ADIM: Checkbox filtrelerini path parçalarına çevir.
        // Bu haritalar, checkbox'ın 'value' değerini URL'de kullanılacak metne çevirir.
        const fuelTypeMap = { '1': 'benzin', '2': 'dizel', '3': 'lpg' };
        const gearTypeMap = { '1': 'manuel', '2': 'otomatik' };

        // Seçili yakıt tiplerini metne çevirip '-' ile birleştir.
        const selectedFuels = filters.fuelTypes.map(val => fuelTypeMap[val]).join('-');
        if (selectedFuels) {
            pathParts.push(selectedFuels);
        }

        // Seçili vites tiplerini metne çevirip '-' ile birleştir.
        const selectedGears = filters.gearTypes.map(val => gearTypeMap[val]).join('-');
        if (selectedGears) {
            pathParts.push(selectedGears);
        }

        // 3. ADIM: Diğer filtreleri URL parametreleri olarak hazırla (? sonrası).
        const params = new URLSearchParams();
        if (filters.yearMin) params.append('a5_min', filters.yearMin);
        if (filters.yearMax) params.append('a5_max', filters.yearMax);
        if (filters.kmMax) params.append('a4_max', filters.kmMax);

        // 4. ADIM: Tüm parçaları birleştirerek nihai URL'i oluştur.
        const finalPath = pathParts.join('/');
        const paramString = params.toString();
        
        let finalUrl = `${baseDomain}/${finalPath}`;
        if (paramString) {
            finalUrl += `?${paramString}`;
        }
        
        console.log("OLUŞTURULAN NİHAİ VE DOĞRU URL:", finalUrl);
        window.open(finalUrl, '_blank');
    };

    return (
        <div className="market-page-wrapper">
            <div className="market-page-container">
                {/* JSX Kısmı (Form) aynı kalabilir, burada değişiklik yok */}
                <div className="card">
                    <h3>Piyasa Analizi için Araç Filtrele</h3>
                    <p className="description">
                        En doğru sonuçlar için aşağıdaki tüm alanları doldurmaya çalışın.
                    </p>

                    <div className="filter-grid">
                        <div className="form-group">
                            <label>Marka</label>
                            <input name="brand" value={filters.brand} onChange={handleInputChange} placeholder="örn: Audi" />
                        </div>
                        <div className="form-group">
                            <label>Model</label>
                            <input name="model" value={filters.model} onChange={handleInputChange} placeholder="örn: A6" />
                        </div>
                        <div className="form-group">
                            <label>Seri (Kasa Tipi)</label>
                            <input name="series" value={filters.series} onChange={handleInputChange} placeholder="örn: A6 Sedan" />
                        </div>
                        <div className="form-group">
                            <label>Donanım Paketi (Motor)</label>
                            <input name="trimPackage" value={filters.trimPackage} onChange={handleInputChange} placeholder="örn: 2.0 TFSI" />
                        </div>
                        <div className="form-group">
                            <label>Yıl (En Az)</label>
                            <input name="yearMin" type="number" value={filters.yearMin} onChange={handleInputChange} placeholder="örn: 2010" />
                        </div>
                        <div className="form-group">
                            <label>Yıl (En Çok)</label>
                            <input name="yearMax" type="number" value={filters.yearMax} onChange={handleInputChange} placeholder="örn: 2020" />
                        </div>
                        <div className="form-group">
                            <label>Kilometre (En Çok)</label>
                            <input name="kmMax" type="number" value={filters.kmMax} onChange={handleInputChange} placeholder="örn: 150000" />
                        </div>
                        
                        <div className="checkbox-group">
                            <label>Yakıt Tipi</label>
                            <div>
                                <label><input type="checkbox" name="fuelTypes" value="1" onChange={handleCheckboxChange} /> Benzin</label>
                                <label><input type="checkbox" name="fuelTypes" value="2" onChange={handleCheckboxChange} /> Dizel</label>
                                <label><input type="checkbox" name="fuelTypes" value="3" onChange={handleCheckboxChange} /> LPG</label>
                            </div>
                        </div>
                        <div className="checkbox-group">
                            <label>Vites Tipi</label>
                            <div>
                                <label><input type="checkbox" name="gearTypes" value="1" onChange={handleCheckboxChange} /> Manuel</label>
                                <label><input type="checkbox" name="gearTypes" value="2" onChange={handleCheckboxChange} /> Otomatik</label>
                            </div>
                        </div>
                    </div>
                    
                    <div className="button-container">
                        <button className="redirect-button" onClick={redirectToSahibinden}>
                            Sahibinden.com'da Piyasayı Gör
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default MarketValuePage;
