import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { jwtDecode } from 'jwt-decode';
import './ChatModal.css'; // Bu CSS'i bir sonraki adımda oluşturacağız

const socket = io('http://localhost:5000'); // Backend sunucu adresimiz

function ChatModal({ vehicle, closeModal }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [currentUser, setCurrentUser] = useState(null);
  const messagesEndRef = useRef(null);

  // Bu sohbetin benzersiz kimliği (admin ID'si şimdilik 1 varsayılıyor)
  const conversationId = `user_${currentUser?.id}-admin_1-vehicle_${vehicle.id}`;

  useEffect(() => {
    // Giriş yapmış kullanıcının bilgilerini token'dan al
    const token = localStorage.getItem('token');
    if (token) {
      // Not: user tablosunda id'nin auto_increment ve primary key olduğundan emin olmalısın
      // Bu örnekte, token'a user.id'yi de eklememiz gerekecek
      const decoded = jwtDecode(token); 
      setCurrentUser(decoded); 
    }

    // Odaya katıl
    socket.emit('join_room', conversationId);

    // Geçmiş mesajları yükle
    socket.on('load_messages', (loadedMessages) => {
      setMessages(loadedMessages);
    });

    // Yeni gelen mesajları dinle
    socket.on('receive_message', (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    // Component kapandığında event listener'ları temizle
    return () => {
      socket.off('load_messages');
      socket.off('receive_message');
    };
  }, [conversationId]);

  // Her yeni mesaj geldiğinde en alta kaydır
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim() === '' || !currentUser) return;

    const messageData = {
      conversation_id: conversationId,
      // Bu ID'ler veritabanındaki users tablosundan gelmeli
      sender_id: currentUser.id, 
      receiver_id: 1, // Admin'in ID'si (varsayım)
      vehicle_id: vehicle.id,
      message: newMessage,
    };

    socket.emit('send_message', messageData);
    setNewMessage('');
  };

  return (
    <div className="chat-modal-overlay" onClick={closeModal}>
      <div className="chat-modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="chat-header">
          <h3>{vehicle.brand} {vehicle.model}</h3>
          <span>Satıcı ile Mesajlaşma</span>
          <button onClick={closeModal} className="close-chat-btn">×</button>
        </div>
        <div className="chat-messages">
          {messages.map((msg) => (
            <div
              key={msg.id}
              className={`message-bubble ${msg.sender_id === currentUser?.id ? 'sent' : 'received'}`}
            >
              <p>{msg.message}</p>
              <span className="message-time">{new Date(msg.created_at).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
        <form className="chat-input-form" onSubmit={handleSendMessage}>
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Mesajınızı yazın..."
          />
          <button type="submit">Gönder</button>
        </form>
      </div>
    </div>
  );
}

export default ChatModal;
