import React, { useState } from 'react';
import jwt_decode from 'jwt-decode';  // Burada düzeltme yaptık

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const response = await fetch('http://localhost:3001/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        const data = await response.json();
        setError(data.message || 'Giriş başarısız!');
        return;
      }

      const data = await response.json();
      const token = data.token;

      if (!token) {
        setError('Token alınamadı.');
        return;
      }

      const decodedToken = jwt_decode(token);  // Burada jwtDecode değil jwt_decode kullanılıyor

      if (decodedToken.role !== 'admin') {
        setError('Bu alan sadece yöneticilere aittir.');
        return;
      }

      // Token'ı localStorage veya istediğin başka bir yere kaydedebilirsin
      localStorage.setItem('token', token);
      setSuccess('Giriş başarılı!');

      // Giriş başarılıysa yönlendirme veya başka işlemler burada yapılabilir

    } catch (error) {
      setError('Sunucu hatası: ' + error.message);
    }
  };

  return (
    <div>
      <h2>Giriş Yap</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Email:</label><br />
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Şifre:</label><br />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <button type="submit">Giriş</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
    </div>
  );
}

export default LoginPage;
