import React, { useState } from 'react';
import axios from 'axios';
import './LoginPage.css';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
const [registerName, setRegisterName] = useState('');
const [registerEmail, setRegisterEmail] = useState('');
const [registerPassword, setRegisterPassword] = useState('');

function LoginPage() {
  // Sadece hangi sekmenin aktif olduğunu tutan tek bir state
  const [activeTab, setActiveTab] = useState('admin'); // 'admin' veya 'user'

  // Her form için ayrı state'ler. Bu, karışıklığı önler.
  const [adminEmail, setAdminEmail] = useState('');
  const [adminPassword, setAdminPassword] = useState('');
  
  const [userEmail, setUserEmail] = useState('');
  const [userPassword, setUserPassword] = useState('');

  const [error, setError] = useState('');
  const navigate = useNavigate();

  // YÖNETİCİ GİRİŞ FONKSİYONU
  const handleAdminLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('http://localhost:5000/api/login', { email: adminEmail, password: adminPassword });
      const token = res.data.token;
      const decodedToken = jwtDecode(token);

      // Sadece admin ise devam et, değilse hata ver.
      if (decodedToken.role !== 'admin') {
        setError('Bu alan sadece yöneticilere aittir.');
        return;
      }

      localStorage.setItem('token', token);
      navigate('/dashboard', { replace: true });

    } catch (err) {
      setError(err.response?.data?.message || 'Giriş işlemi başarısız');
    }
  };

  // KULLANICI GİRİŞ FONKSİYONU
  const handleUserLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('http://localhost:5000/api/login', { email: userEmail, password: userPassword });
      const token = res.data.token;
      // Kullanıcının admin olup olmadığını kontrol etmeye gerek yok, backend zaten doğru rolü veriyor.
      localStorage.setItem('token', token);
      navigate('/home', { replace: true });
    } catch (err) {
      setError(err.response?.data?.message || 'Giriş işlemi başarısız');
    }
  };

  const clearErrors = () => setError('');
  


  return (
    <div className="login-container">
      <div className="login-wrapper">
        <div className="role-tabs">
          <button 
            className={`tab-button ${activeTab === 'admin' ? 'active' : ''}`}
            onClick={() => { setActiveTab('admin'); clearErrors(); }}
          >
            Yönetici Girişi
          </button>
          <button 
            className={`tab-button ${activeTab === 'user' ? 'active' : ''}`}
            onClick={() => { setActiveTab('user'); clearErrors(); }}
          >
            Kullanıcı Girişi
          </button>
        </div>

        {/* === YÖNETİCİ GİRİŞ FORMU === */}
        {activeTab === 'admin' && (
          <form className="login-form" onSubmit={handleAdminLogin}>
            <h2>Yönetici Girişi</h2>
            <input
              type="email"
              placeholder="E-posta Adresi"
              value={adminEmail}
              onChange={(e) => setAdminEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Şifre"
              value={adminPassword}
              onChange={(e) => setAdminPassword(e.target.value)}
              required
            />
            <button type="submit">Giriş Yap</button>
            {error && <p className="error">{error}</p>}
            <div className="form-switch admin-switch">
              {/* Yönetici formunda sadece "Şifremi Unuttum" var */}
              <span onClick={() => navigate('/forgot-password')}>Şifremi Unuttum</span>
            </div>
          </form>
        )}

        {/* === KULLANICI GİRİŞ FORMU === */}
        {activeTab === 'user' && (
          <form className="login-form" onSubmit={handleUserLogin}>
            <h2>Kullanıcı Girişi</h2>
            <input
              type="email"
              placeholder="E-posta Adresi"
              value={userEmail}
              onChange={(e) => setUserEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Şifre"
              value={userPassword}
              onChange={(e) => setUserPassword(e.target.value)}
              required
            />
            <button type="submit">Giriş Yap</button>
            {error && <p className="error">{error}</p>}
            {/* Kullanıcı formunda hepsi var */}
            <div className="form-switch">
              <button type="button" onClick={() => setActiveTab('register')}>
  Kaydol
</button>
              <button type="button" onClick={() => navigate('/forgot-password')}>
                Şifremi Unuttum
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}

export default LoginPage;
