import { io } from 'socket.io-client';

// Backend sunucunuzun adresi
const URL = 'http://localhost:5000';

// autoConnect: false -> Bağlantıyı biz ne zaman istersek o zaman başlatmasını sağlıyoruz.
// Bu, gereksiz bağlantıları önler ve uygulamanın performansını artırır.
export const socket = io(URL, {
  autoConnect: false,
});
