import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { jwtDecode } from 'jwt-decode';
import './AdminChatBox.css';

const socket = io('http://localhost:5000');

function AdminChatBox({ conversationId }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [adminUser, setAdminUser] = useState(null);
  const [conversationInfo, setConversationInfo] = useState({});
  const messagesEndRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setAdminUser(decoded);
      } catch (error) {
        console.error("Admin token decode error:", error);
      }
    }
  }, []);

  useEffect(() => {
    // Sadece conversationId değiştiğinde çalış
    if (conversationId && adminUser) {
      const parts = conversationId.split('-');
      const userIdPart = parts.find(p => p.startsWith('user_'));
      const vehicleIdPart = parts.find(p => p.startsWith('vehicle_'));
      
      const userId = userIdPart ? userIdPart.split('_')[1] : null;
      const vehicleId = vehicleIdPart ? vehicleIdPart.split('_')[1] : null;
      setConversationInfo({ userId, vehicleId });

      socket.emit('join_room', conversationId);

      const handleLoadMessages = (loadedMessages) => {
        setMessages(loadedMessages);
      };

      const handleReceiveMessage = (message) => {
        // Sadece mevcut sohbetimize aitse mesajı ekle
        if (message.conversation_id === conversationId) {
            setMessages((prev) => [...prev, message]);
        }
      };

      socket.on('load_messages', handleLoadMessages);
      socket.on('receive_message', handleReceiveMessage);

      // Component unmount olduğunda veya conversationId değiştiğinde listener'ları kaldır
      return () => {
        socket.off('load_messages', handleLoadMessages);
        socket.off('receive_message', handleReceiveMessage);
        // İsteğe bağlı: odadan ayrılma eventi de eklenebilir
        // socket.emit('leave_room', conversationId);
      };
    }
  }, [conversationId, adminUser]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim() === '' || !adminUser || !conversationInfo.userId) return;
    
    const messageData = {
      conversation_id: conversationId,
      sender_id: adminUser.id,
      receiver_id: parseInt(conversationInfo.userId),
      vehicle_id: conversationInfo.vehicleId ? parseInt(conversationInfo.vehicleId) : null,
      message: newMessage.trim(),
    };

    socket.emit('send_message', messageData);
    setNewMessage('');
  };

  if (!conversationId) {
    return (
      <div className="admin-chat-box-empty">
        <p>Görüntülemek için bir konuşma seçin.</p>
      </div>
    );
  }

  return (
    <div className="admin-chat-box">
      <div className="chat-messages-admin">
        {messages.map((msg, index) => {
          const isAdminMessage = parseInt(msg.sender_id) === adminUser?.id;
          return (
            <div
              key={msg.id || index}
              className={`message-container ${isAdminMessage ? 'admin-message' : 'user-message'}`}
            >
              <div className={`message-bubble ${isAdminMessage ? 'admin-bubble' : 'user-bubble'}`}>
                <p>{msg.message}</p>
                <span className="message-time">
                  {new Date(msg.created_at).toLocaleTimeString('tr-TR', { hour: '2-digit', minute: '2-digit' })}
                </span>
              </div>
            </div>
          );
        })}
        <div ref={messagesEndRef} />
      </div>
      
      <form className="chat-input-form-admin" onSubmit={handleSendMessage}>
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Cevabınızı yazın..."
        />
        <button type="submit" disabled={!newMessage.trim()}>
          ➢
        </button>
      </form>
    </div>
  );
}

export default AdminChatBox;
