import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AdminChatBox from './AdminChatBox';
import './MessagesPage.css';

function MessagesPage() {
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchConversations = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Token bulunamadÄ±');
        return;
      }

      const response = await axios.get('http://localhost:5000/api/conversations', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      
      console.log('KonuÅŸmalar:', response.data);
      setConversations(response.data);
      setError('');
    } catch (error) {
      console.error("KonuÅŸmalar yÃ¼klenemedi:", error);
      setError('KonuÅŸmalar yÃ¼klenemedi');
      
      // Token geÃ§ersizse login'e yÃ¶nlendir
      if (error.response?.status === 403 || error.response?.status === 401) {
        localStorage.removeItem('token');
        window.location.href = '/';
      }
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchConversations();
    
    // Her 30 saniyede bir konuÅŸmalarÄ± gÃ¼ncelle
    const interval = setInterval(fetchConversations, 30000);
    
    return () => clearInterval(interval);
  }, []);

  const handleConversationSelect = (conversation) => {
    console.log('SeÃ§ilen konuÅŸma:', conversation);
    setSelectedConversation(conversation);
  };

  const refreshConversations = () => {
    setIsLoading(true);
    fetchConversations();
  };

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Gelen Kutusu YÃ¼kleniyor...</div>
      </div>
    );
  }

  if (error && conversations.length === 0) {
    return (
      <div className="error-container">
        <p>Hata: {error}</p>
        <button onClick={refreshConversations}>Tekrar Dene</button>
      </div>
    );
  }

  return (
    <div className="messages-page-container">
      <div className="conversations-sidebar">
        <div className="sidebar-header">
          <h2>Gelen Kutusu</h2>
          <button onClick={refreshConversations} className="refresh-btn" title="Yenile">
            ðŸ”„
          </button>
        </div>
        
        <div className="conversations-list">
          {conversations.length === 0 ? (
            <div className="no-conversations">
              <p>HenÃ¼z mesajÄ±nÄ±z yok.</p>
            </div>
          ) : (
            conversations.map((conversation) => (
              <div 
                key={conversation.conversation_id} 
                className={`conversation-item ${
                  selectedConversation?.conversation_id === conversation.conversation_id ? 'active' : ''
                }`}
                onClick={() => handleConversationSelect(conversation)}
              >
                <div className="conversation-avatar">
                  {conversation.user_name?.charAt(0).toUpperCase() || 'U'}
                </div>
                
                <div className="conversation-content">
                  <div className="conversation-header">
                    <h4>{conversation.user_name || 'Anonim KullanÄ±cÄ±'}</h4>
                    <span className="conversation-time">
                      {new Date(conversation.created_at).toLocaleString('tr-TR', {
                        hour: '2-digit',
                        minute: '2-digit',
                        day: '2-digit',
                        month: '2-digit'
                      })}
                    </span>
                  </div>
                  
                  <div className="vehicle-info">
                    <span className="vehicle-name">{conversation.brand} {conversation.model}</span>
                  </div>
                  
                  <p className="last-message">{conversation.message}</p>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      <div className="chat-area">
        {selectedConversation ? (
          <AdminChatBox 
            key={selectedConversation.conversation_id}
            conversationId={selectedConversation.conversation_id}
            vehicleInfo={{
              vehicle_id: selectedConversation.vehicle_id,
              brand: selectedConversation.brand,
              model: selectedConversation.model
            }}
            userInfo={{
              user_id: selectedConversation.user_id,
              user_name: selectedConversation.user_name
            }}
          />
        ) : (
          <div className="no-chat-selected">
            <div className="no-chat-content">
              <h3>HoÅŸ Geldiniz!</h3>
              <p>MesajlaÅŸmaya baÅŸlamak iÃ§in sol taraftan bir konuÅŸma seÃ§in.</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default MessagesPage;
