import React, { useState } from 'react';
import axios from 'axios';
import './LoginPage.css';

function LoginPage() {
  const [formType, setFormType] = useState('login'); 
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [securityAnswer, setSecurityAnswer] = useState('');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  const clearFields = () => {
    setEmail('');
    setPassword('');
    setSecurityAnswer('');
    setError('');
    setMessage('');
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/login', { email, password });
      localStorage.setItem('token', res.data.token);
      window.location.href = '/dashboard';
    } catch (err) {
      setError(err.response?.data?.message || 'Giriş hatası');
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/register', {
        email,
        password,
        securityAnswer
      });
      setMessage(res.data.message);
      clearFields();
    } catch (err) {
      setError(err.response?.data?.message || 'Kayıt hatası');
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={formType === 'login' ? handleLogin : handleRegister}>
        <h2>{formType === 'login' ? 'Giriş Yap' : 'Kaydol'}</h2>

        <input
          type="email"
          placeholder="E-posta"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />

        <input
          type="password"
          placeholder="Şifre"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        {formType === 'register' && (
          <input
            type="text"
            placeholder="İlk evcil hayvanınızın adı?"
            value={securityAnswer}
            onChange={(e) => setSecurityAnswer(e.target.value)}
            required
          />
        )}

        <button type="submit">
          {formType === 'login' ? 'Giriş Yap' : 'Kaydol'}
        </button>

        {error && <p className="error">{error}</p>}
        {message && <p className="success">{message}</p>}

        <div className="form-switch">
          <button
            type="button"
            onClick={() => {
              setFormType(formType === 'login' ? 'register' : 'login');
              clearFields();
            }}
          >
            {formType === 'login' ? 'Kaydol' : 'Giriş Yap'}
          </button>

          <button type="button" onClick={() => window.location.href = '/forgot-password'}>
            Şifremi Unuttum
          </button>
        </div>
      </form>
    </div>
  );
}

export default LoginPage;
