import React, { useState } from 'react';
import './PiyasaKarsilastirma.css'; // Stil dosyamızı import ediyoruz.

const carBrands = [
    "Alfa Romeo", "Aston Martin", "Audi", "Bentley", "BMW", "Chery", "Chevrolet", 
    "Citroen", "Dacia", "DS Automobiles", "Fiat", "Ford", "Honda", "Hyundai", "Jaguar", "Jeep", 
    "Kia", "Land Rover", "Lexus", "Mercedes-Benz", "MG", "Mini", "Nissan", "Opel", 
    "Peugeot", "Porsche", "Renault", "Seat", "Skoda", "Subaru", "Suzuki", "Togg", 
    "Toyota", "Volkswagen", "Volvo"
].sort();

function PiyasaKarsilastirma() {
    const [filters, setFilters] = useState({
        brand: '',
        model: '',
        yearMin: '',
        yearMax: '',
        kmMin: '',
        kmMax: '',
        gear: '', // Değerler: manuel, otomatik, yari-otomatik
        fuel: '', // Değerler: benzin, dizel, lpg, elektrik
    });

    const handleInputChange = (e) => {
        const { name, value, type } = e.target;
        const processedValue = type === 'text' ? value.toUpperCase() : value;
        setFilters(prev => ({ ...prev, [name]: processedValue }));
    };

    const formatForUrl = (text) => text.trim().toLowerCase().replace(/\s+/g, '-');

    // --- SAHİBİNDEN.COM İÇİN TAMAMEN YENİLENMİŞ FONKSİYON ---
    const redirectToSahibinden = () => {
        let baseUrl = 'https://www.sahibinden.com/otomobil';

        // Sahibinden.com'un filtreler için kullandığı gerçek ID'ler.
        const sahibindenFuelMap = { 'benzin': 1, 'dizel': 2, 'lpg': 3, 'elektrik': 4 };
        const sahibindenGearMap = { 'manuel': 1, 'yari-otomatik': 3, 'otomatik': 2 };

        // Marka ve model varsa URL yoluna ekliyoruz.
        if (filters.brand) {
            baseUrl += `/${formatForUrl(filters.brand)}`;
            if (filters.model) {
                baseUrl += `-${formatForUrl(filters.model)}`;
            }
        }
        
        const params = new URLSearchParams();
        if (filters.yearMin) params.append('a5_min', filters.yearMin);
        if (filters.yearMax) params.append('a5_max', filters.yearMax);
        if (filters.kmMin) params.append('a4_min', filters.kmMin);
        if (filters.kmMax) params.append('a4_max', filters.kmMax);

        // Yakıt ve Vites filtrelerini doğru ID'lerle parametre olarak ekliyoruz.
        if (filters.fuel && sahibindenFuelMap[filters.fuel]) {
            params.append('a3', sahibindenFuelMap[filters.fuel]);
        }
        if (filters.gear && sahibindenGearMap[filters.gear]) {
            params.append('a2', sahibindenGearMap[filters.gear]);
        }
        
        let finalUrl = baseUrl;
        if (params.toString()) {
            finalUrl += `?${params.toString()}`;
        }

        console.log("Sahibinden URL:", finalUrl);
        window.open(finalUrl, '_blank');
    };

    // --- ARABAM.COM İÇİN TAMAMEN YENİLENMİŞ FONKSİYON ---
    const redirectToArabamCom = () => {
        let baseUrl = 'https://www.arabam.com/ikinci-el/otomobil';

        if (filters.brand) {
            baseUrl += `/${formatForUrl(filters.brand)}`;
            if (filters.model) {
                baseUrl += `-${formatForUrl(filters.model)}`;
            }
        }
        
        // Arabam.com için TÜM filtreleri parametre olarak ekliyoruz.
        const params = new URLSearchParams();
        if (filters.yearMin) params.append('minYear', filters.yearMin);
        if (filters.yearMax) params.append('maxYear', filters.yearMax);
        if (filters.kmMin) params.append('minKm', filters.kmMin);
        if (filters.kmMax) params.append('maxKm', filters.kmMax);

        // Sayısal ID yerine, doğrudan metinsel değerleri gönderiyoruz.
        if (filters.fuel) params.append('fuel', filters.fuel);
        if (filters.gear) params.append('gear', filters.gear);
        
        let finalUrl = baseUrl;
        if (params.toString()) {
            finalUrl += `?${params.toString()}`;
        }

        console.log("Arabam.com URL:", finalUrl);
        window.open(finalUrl, '_blank');
    };


    return (
        <div className="comparison-wrapper">
            <div className="comparison-card">
                <h2>Araç Piyasa Değeri Karşılaştırma</h2>
                <p>Filtreleri doldurarak Sahibinden.com ve Arabam.com'daki piyasa değerlerini anında görün.</p>
                
                <div className="filter-grid">
                    <div className="form-group">
                        <label>Marka</label>
                        <select name="brand" value={filters.brand} onChange={handleInputChange}>
                            <option value="">TÜM MARKALAR</option>
                            {carBrands.map(brand => (
                                <option key={brand} value={brand}>{brand}</option>
                            ))}
                        </select>
                    </div>
                    <div className="form-group">
                        <label>Model</label>
                        <input type="text" name="model" value={filters.model} onChange={handleInputChange} placeholder="örn: ASTRA" />
                    </div>
                    <div className="form-group">
                        <label>Vites Tipi</label>
                        <select name="gear" value={filters.gear} onChange={handleInputChange}>
                            <option value="">TÜMÜ</option>
                            <option value="manuel">MANUEL</option>
                            <option value="otomatik">OTOMATİK</option>
                            <option value="yari-otomatik">YARI OTOMATİK</option>
                        </select>
                    </div>
                    <div className="form-group">
                        <label>Yakıt Tipi</label>
                        <select name="fuel" value={filters.fuel} onChange={handleInputChange}>
                            <option value="">TÜMÜ</option>
                            <option value="benzin">BENZİN</option>
                            <option value="dizel">DİZEL</option>
                            <option value="elektrik">ELEKTRİK</option>
                            <option value="lpg">BENZİN & LPG</option>
                        </select>
                    </div>
                    <div className="form-group">
                        <label>Yıl (En Az)</label>
                        <input type="number" name="yearMin" value={filters.yearMin} onChange={handleInputChange} placeholder="örn: 2015" />
                    </div>
                    <div className="form-group">
                        <label>Yıl (En Çok)</label>
                        <input type="number" name="yearMax" value={filters.yearMax} onChange={handleInputChange} placeholder="örn: 2020" />
                    </div>
                     <div className="form-group">
                        <label>KM (En Az)</label>
                        <input type="number" name="kmMin" value={filters.kmMin} onChange={handleInputChange} placeholder="örn: 50000" />
                    </div>
                    <div className="form-group">
                        <label>KM (En Çok)</label>
                        <input type="number" name="kmMax" value={filters.kmMax} onChange={handleInputChange} placeholder="örn: 150000" />
                    </div>
                </div>

                <div className="button-container">
                    <button className="search-button sahibinden-button" onClick={redirectToSahibinden}>
                        Sahibinden.com'da Ara
                    </button>
                    <button className="search-button arabam-button" onClick={redirectToArabamCom}>
                        Arabam.com'da Ara
                    </button>
                </div>
            </div>
        </div>
    );
}

export default PiyasaKarsilastirma;
