import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './PersonnelPage.css'; // Stil dosyasını import ediyoruz

function PersonnelPage() {
    // ===== STATE (DURUM) YÖNETİMİ =====
    const [personnelList, setPersonnelList] = useState([]); // Backend'den gelen tüm personeli tutar
    const [isEditing, setIsEditing] = useState(false); // Düzenleme modunda olup olmadığımızı tutar
    const [currentPersonnelId, setCurrentPersonnelId] = useState(null); // Düzenlenen personelin ID'sini tutar
    const [searchTerm, setSearchTerm] = useState(''); // Filtre input'unun değerini tutar

    // Formun başlangıçtaki boş hali için bir şablon
    const initialFormState = {
        ad: '', soyad: '', tc_kimlik: '', telefon: '', adres: '',
        pozisyon: '', maas: '', ise_baslama_tarihi: ''
    };
    const [formData, setFormData] = useState(initialFormState);


    // ===== API FONKSİYONLARI =====
    // Backend'den tüm personeli çeken fonksiyon
    const fetchPersonnel = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/personnel');
            setPersonnelList(response.data);
        } catch (error) {
            console.error("Personel listesi çekilirken hata oluştu:", error);
        }
    };

    // Bu useEffect, component ilk yüklendiğinde personeli otomatik olarak çeker
    useEffect(() => {
        fetchPersonnel();
    }, []); // Boş dizi, bu fonksiyonun sadece bir kez çalışmasını sağlar


    // ===== OLAY YÖNETİCİLERİ (EVENT HANDLERS) =====
    // Formdaki herhangi bir input değiştiğinde çalışır
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    // "Kaydet" veya "Güncelle" butonuna basıldığında çalışır
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (isEditing) {
                // Güncelleme işlemi
                await axios.put(`http://localhost:5000/api/personnel/${currentPersonnelId}`, formData);
                alert('Personel başarıyla güncellendi!');
            } else {
                // Yeni kayıt ekleme işlemi
                await axios.post('http://localhost:5000/api/personnel', formData);
                alert('Personel başarıyla eklendi!');
            }
            cancelEdit(); // Formu temizle ve düzenleme modundan çık
            fetchPersonnel(); // Tabloyu yenile
        } catch (error) {
            console.error("İşlem sırasında hata:", error);
            // Backend'den gelen özel hata mesajını göster (Örn: TC zaten kayıtlı)
            alert(error.response?.data?.message || 'Bir hata oluştu.');
        }
    };

    // Tablodaki "Düzenle" butonuna tıklandığında çalışır
    const handleEdit = (person) => {
        setIsEditing(true);
        setCurrentPersonnelId(person.id);
        // Tarih formatını input[type=date]'in anlayacağı YYYY-MM-DD formatına çevir
        const formattedPerson = {
            ...person,
            ise_baslama_tarihi: new Date(person.ise_baslama_tarihi).toISOString().split('T')[0]
        };
        setFormData(formattedPerson);
        window.scrollTo(0, 0); // Kullanıcının formu görmesi için sayfanın en üstüne git
    };

    // Tablodaki "Sil" butonuna tıklandığında çalışır
    const handleDelete = async (id) => {
        if (window.confirm("Bu personeli silmek istediğinizden emin misiniz?")) {
            try {
                await axios.delete(`http://localhost:5000/api/personnel/${id}`);
                alert("Personel başarıyla silindi.");
                fetchPersonnel(); // Tabloyu yenile
            } catch (error) {
                console.error("Silme hatası:", error);
                alert("Personel silinirken bir hata oluştu.");
            }
        }
    };

    // Düzenleme işlemini iptal eder veya formu temizler
    const cancelEdit = () => {
        setIsEditing(false);
        setCurrentPersonnelId(null);
        setFormData(initialFormState);
    };


    // ===== FİLTRELEME MANTIĞI =====
    // Arama kutusuna yazılan metne göre personeli filtreler
    const filteredPersonnel = personnelList.filter(p => 
        p.ad.toLowerCase().includes(searchTerm.toLowerCase()) ||
        p.soyad.toLowerCase().includes(searchTerm.toLowerCase()) ||
        p.tc_kimlik.includes(searchTerm) ||
        p.telefon.includes(searchTerm) ||
        p.pozisyon.toLowerCase().includes(searchTerm.toLowerCase())
    );


    // ===== JSX RENDER KISMI =====
    return (
        <div className="personnel-page-wrapper">
            <div className="personnel-page">
                {/* --- FORM KARTI --- */}
                <div className="card form-card">
                    <h2>{isEditing ? 'Personel Bilgilerini Düzenle' : 'Yeni Personel Kayıt'}</h2>
                    <form onSubmit={handleSubmit}>
                        <div className="form-grid">
                            <input name="ad" value={formData.ad} onChange={handleInputChange} placeholder="Ad" required />
                            <input name="soyad" value={formData.soyad} onChange={handleInputChange} placeholder="Soyad" required />
                            <input name="tc_kimlik" value={formData.tc_kimlik} onChange={handleInputChange} placeholder="TC Kimlik No" required maxLength="11" />
                            <input name="telefon" value={formData.telefon} onChange={handleInputChange} placeholder="Telefon" required />
                            <input name="pozisyon" value={formData.pozisyon} onChange={handleInputChange} placeholder="Pozisyon" />
                            <input name="maas" type="number" step="0.01" value={formData.maas} onChange={handleInputChange} placeholder="Maaş" />
                            <input name="ise_baslama_tarihi" type="date" value={formData.ise_baslama_tarihi} onChange={handleInputChange} required />
                            <textarea name="adres" value={formData.adres} onChange={handleInputChange} placeholder="Adres" className="address-area"></textarea>
                        </div>
                        <div className="form-buttons">
                            <button type="submit" className="submit-btn">{isEditing ? 'Bilgileri Güncelle' : 'Personeli Kaydet'}</button>
                            {isEditing && <button type="button" className="cancel-btn" onClick={cancelEdit}>İptal</button>}
                        </div>
                    </form>
                </div>

                {/* --- TABLO KARTI --- */}
                <div className="card table-card">
                    <div className="table-header">
                        <h2>Kayıtlı Personeller</h2>
                        <input 
                            type="text" 
                            placeholder="Personel Filtrele (Ad, Soyad, TC...)" 
                            className="filter-input"
                            value={searchTerm}
                            onChange={e => setSearchTerm(e.target.value)}
                        />
                    </div>
                    <div className="table-wrapper">
                        <table>
                            <thead>
                                <tr>
                                    <th>Ad Soyad</th>
                                    <th>TC Kimlik</th>
                                    <th>Telefon</th>
                                    <th>Pozisyon</th>
                                    <th>İşe Başlama</th>
                                    <th>Adres</th>
                                    <th>İşlemler</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filteredPersonnel.map(person => (
                                    <tr key={person.id}>
                                        <td>{person.ad} {person.soyad}</td>
                                        <td>{person.tc_kimlik}</td>
                                        <td>{person.telefon}</td>
                                        <td>{person.pozisyon}</td>
                                        <td>{new Date(person.ise_baslama_tarihi).toLocaleDateString('tr-TR')}</td>
                                        <td>{person.adres}</td>
                                        <td>
                                            <div className="action-buttons">
                                                <button className="edit-btn" onClick={() => handleEdit(person)}>Düzenle</button>
                                                <button className="delete-btn" onClick={() => handleDelete(person.id)}>Sil</button>
                                            </div>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default PersonnelPage;
