import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';

// KORUYUCU BİLEŞENLER
// Bu iki dosyanın `src` klasörünüzün içinde olduğundan emin olun.
// Eğer `src/components` içindelerse, yolu ona göre güncelleyin.
import ProtectedRoute from './ProtectedRoute'; 
import AdminProtectedRoute from './AdminProtectedRoute'; 

// GENEL SAYFALAR (Giriş yapmamış herkes erişebilir)
import LoginPage from './LoginPage';
import ForgotPasswordPage from './ForgotPasswordPage';

// KULLANICIYA ÖZEL SAYFALAR (Sadece giriş yapmış kullanıcılar erişebilir)
import HomePage from './HomePage'; // Tüm ilanların listelendiği ana sayfa

// YÖNETİCİYE ÖZEL SAYFALAR (Sadece adminler erişebilir)
import AdminDashboard from './AdminDashboard';      // Admin ana paneli
import VehiclesPage from './VehiclesPage';          // Admin'in araç ekleyip/yönettiği sayfa
import PersonnelPage from './PersonnelPage';        // Admin'in personel yönettiği sayfa
import CreditCalculatorPage from './CreditCalculatorPage';
import MarketValuePage from './MarketValuePage';

function App() {
  return (
    <Router>
      <Routes>
        {/* ======================================= */}
        {/*      1. HERKESE AÇIK ROTALAR            */}
        {/* ======================================= */}
        <Route path="/" element={<LoginPage />} />
        <Route path="/forgot-password" element={<ForgotPasswordPage />} />


        {/* ======================================= */}
        {/*      2. KULLANICIYA ÖZEL ROTALAR        */}
        {/* ======================================= */}
        {/* Normal kullanıcı giriş yapınca bu sayfaya yönlendirilir. */}
        {/* `ProtectedRoute` sayesinde, giriş yapmamış kimse bu sayfayı göremez. */}
        <Route 
          path="/home" 
          element={
            <ProtectedRoute>
              <HomePage />
            </ProtectedRoute>
          } 
        />


        {/* ======================================= */}
        {/*      3. YÖNETİCİYE ÖZEL ROTALAR         */}
        {/* ======================================= */}
        {/* Bu rotalar `AdminProtectedRoute` ile korunur. Sadece adminler erişebilir. */}
        {/* Normal bir kullanıcı bu URL'lere gitmeye çalışırsa, ana sayfasına geri yönlendirilir. */}

        <Route 
          path="/admin-dashboard" 
          element={<AdminProtectedRoute><AdminDashboard /></AdminProtectedRoute>} 
        />
        <Route 
          path="/admin/vehicles" 
          element={<AdminProtectedRoute><VehiclesPage /></AdminProtectedRoute>} 
        />
        <Route 
          path="/admin/personnel" 
          element={<AdminProtectedRoute><PersonnelPage /></AdminProtectedRoute>} 
        />
        <Route 
          path="/admin/kredi"
          element={<AdminProtectedRoute><CreditCalculatorPage /></AdminProtectedRoute>} 
        />
        <Route 
          path="/admin/piyasa" 
          element={<AdminProtectedRoute><MarketValuePage /></AdminProtectedRoute>} 
        />

      </Routes>
    </Router>
  );
}

export default App;
