import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import './VehiclesPage.css'; // CSS dosyamızı import ediyoruz

function VehiclesPage() {
  const [vehicles, setVehicles] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentVehicleId, setCurrentVehicleId] = useState(null);
  const [filter, setFilter] = useState('');

  // Form verileri için başlangıç durumu
  const initialFormData = {
    brand: '', model: '', year: '', color: '', gear: '', fuel: '',
    mileage: '', purchase_price: '', sale_price: '', description: ''
  };
  const [formData, setFormData] = useState(initialFormData);

  // API'den araçları çeken fonksiyon
  const fetchVehicles = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/vehicles');
      setVehicles(response.data);
    } catch (error) {
      console.error("Araçlar çekilirken hata oluştu:", error);
    }
  };

  // Bileşen ilk yüklendiğinde araçları çek
  useEffect(() => {
    fetchVehicles();
  }, []);

  // Formdaki input değişikliklerini yöneten fonksiyon
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({ ...prevData, [name]: value }));
  };

  // Düzenleme modunu iptal etme fonksiyonu
  const cancelEdit = () => {
    setIsEditing(false);
    setCurrentVehicleId(null);
    setFormData(initialFormData); // Formu temizle
  };

  // Düzenleme modunu başlatan fonksiyon
  const handleEdit = (vehicle) => {
    setIsEditing(true);
    setCurrentVehicleId(vehicle.id);
    setFormData(vehicle);
    window.scrollTo(0, 0); // Sayfanın başına git
  };

  // Araç silme fonksiyonu
  const handleDelete = async (id) => {
    if (window.confirm("Bu aracı silmek istediğinizden emin misiniz?")) {
      try {
        await axios.delete(`http://localhost:5000/api/vehicles/${id}`);
        alert("Araç başarıyla silindi.");
        fetchVehicles();
      } catch (error) {
        console.error("Araç silinirken hata oluştu:", error);
        alert("Araç silinirken bir hata oluştu.");
      }
    }
  };

  // Formu gönderme (yeni ekleme veya güncelleme) fonksiyonu
  const handleSubmit = async (e) => {
    e.preventDefault();
    const url = isEditing ? `http://localhost:5000/api/vehicles/${currentVehicleId}` : 'http://localhost:5000/api/vehicles';
    const method = isEditing ? 'put' : 'post';
    const successMessage = isEditing ? 'Araç başarıyla güncellendi!' : 'Araç başarıyla eklendi!';
    const errorMessage = isEditing ? 'Araç güncellenirken bir hata oluştu.' : 'Araç eklenirken bir hata oluştu.';

    try {
      await axios[method](url, formData);
      alert(successMessage);
      cancelEdit();
      fetchVehicles();
    } catch (error) {
      console.error(errorMessage, error);
      alert(errorMessage);
    }
  };

  // Filtrelenmiş araç listesini hesaplayan useMemo
  const filteredVehicles = useMemo(() => {
    const searchTerm = filter.toLowerCase();
    if (!searchTerm) return vehicles;

    return vehicles.filter(vehicle =>
      Object.values(vehicle).some(value =>
        String(value).toLowerCase().includes(searchTerm)
      )
    );
  }, [vehicles, filter]);

  return (
    <div className="vehicle-page-wrapper">
      <div className="vehicle-page">
        
        {/* === ARAÇ KAYIT FORMU === */}
        <div className="form-container">
          <h2 className="page-title">{isEditing ? 'Aracı Düzenle' : 'Araç Kayıt'}</h2>
          <form className="vehicle-form" onSubmit={handleSubmit}>
            <div className="form-row">
              <input type="text" name="brand" placeholder="Marka" value={formData.brand} onChange={handleInputChange} required />
              <input type="text" name="model" placeholder="Model" value={formData.model} onChange={handleInputChange} required />
              <input type="number" name="year" placeholder="Yıl" value={formData.year} onChange={handleInputChange} required />
            </div>
            <div className="form-row">
              <input type="text" name="color" placeholder="Renk" value={formData.color} onChange={handleInputChange} required />
              <input type="text" name="gear" placeholder="Vites" value={formData.gear} onChange={handleInputChange} required />
              <input type="text" name="fuel" placeholder="Yakıt" value={formData.fuel} onChange={handleInputChange} required />
            </div>
            <div className="form-row">
              <input type="number" name="mileage" placeholder="Kilometre" value={formData.mileage} onChange={handleInputChange} required />
              <input type="number" name="purchase_price" placeholder="Alış Fiyatı" value={formData.purchase_price} onChange={handleInputChange} required />
              <input type="number" name="sale_price" placeholder="Satış Fiyatı" value={formData.sale_price} onChange={handleInputChange} required />
            </div>
            <div className="form-row">
              <textarea name="description" placeholder="Açıklama" value={formData.description} onChange={handleInputChange}></textarea>
            </div>
            <div className="form-buttons-container">
              <button type="submit" className="add-vehicle-btn">
                {isEditing ? 'Aracı Güncelle' : 'Araç Ekle'}
              </button>
              {isEditing && (
                <button type="button" className="add-vehicle-btn cancel-edit-btn" onClick={cancelEdit}>
                  İptal
                </button>
              )}
            </div>
          </form>
        </div>

        {/* === KAYITLI ARAÇLAR TABLOSU === */}
        <div className="card table-card">
          <h2>Kayıtlı Araçlar</h2>
          <div className="filter-container">
            <input
              type="text"
              placeholder="Araç listesinde arama yapın (Marka, Model, Yıl...)"
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
            />
          </div>
          <div className="table-wrapper">
            <table className="vehicle-table">
              <thead>
                <tr>
                  <th>Marka</th><th>Model</th><th>Yıl</th><th>Renk</th><th>Vites</th><th>Yakıt</th><th>KM</th><th>Alış</th><th>Satış</th><th>Açıklama</th>
                  <th className="sticky-col">İşlemler</th>
                </tr>
              </thead>
              <tbody>
                {filteredVehicles.map((vehicle) => (
                  <tr key={vehicle.id}>
                    <td>{vehicle.brand}</td>
                    <td>{vehicle.model}</td>
                    <td>{vehicle.year}</td>
                    <td>{vehicle.color}</td>
                    <td>{vehicle.gear}</td>
                    <td>{vehicle.fuel}</td>
                    <td>{vehicle.mileage.toLocaleString('tr-TR')}</td>
                    <td className="price-cell">{parseFloat(vehicle.purchase_price).toLocaleString('tr-TR', { style: 'currency', currency: 'TRY' })}</td>
                    <td className="price-cell">{parseFloat(vehicle.sale_price).toLocaleString('tr-TR', { style: 'currency', currency: 'TRY' })}</td>
                    <td>{vehicle.description}</td>
                    <td className="sticky-col">
                      <div className="action-buttons">
                        <button className="edit-btn" onClick={() => handleEdit(vehicle)}>Düzenle</button>
                        <button className="delete-btn" onClick={() => handleDelete(vehicle.id)}>Sil</button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            {filteredVehicles.length === 0 && (
                <p style={{ textAlign: 'center', padding: '20px' }}>Filtre kriterlerinize uygun araç bulunamadı.</p>
            )}
          </div>
        </div>
        
      </div>
    </div>
  );
}

export default VehiclesPage;
