import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import ChatModal from './ChatModal'; // ChatModal'ı kullanacağız
import './UserMessagesPage.css'; // Stil dosyası (bunu da oluşturmanız gerekebilir)

function UserMessagesPage() {
    const [conversations, setConversations] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedVehicleForChat, setSelectedVehicleForChat] = useState(null);
    const navigate = useNavigate();

    const fetchUserConversations = useCallback(async () => {
        setIsLoading(true);
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/');
                return;
            }
            const response = await axios.get('http://localhost:5000/api/user-conversations', {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            setConversations(response.data);
            setError('');
        } catch (err) {
            console.error("Kullanıcı konuşmaları alınamadı:", err);
            setError(err.response?.data?.message || 'Konuşmalar yüklenirken bir hata oluştu.');
        } finally {
            setIsLoading(false);
        }
    }, [navigate]);

    useEffect(() => {
        fetchUserConversations();
    }, [fetchUserConversations]);

    const handleConversationClick = async (conversation) => {
        if (!conversation.vehicle_id) {
            alert("Bu sohbete ait araç bilgisi bulunamadı.");
            return;
        }
        try {
            // ChatModal'a tam vehicle nesnesi gerektiği için aracı tekrar çekiyoruz.
            const response = await axios.get(`http://localhost:5000/api/vehicles/${conversation.vehicle_id}`);
            setSelectedVehicleForChat(response.data);
        } catch (err) {
            console.error("Araç detayı çekilirken hata oluştu:", err);
            alert("Sohbet açılırken bir hata oluştu.");
        }
    };

    const closeChatModal = () => {
        setSelectedVehicleForChat(null);
        // Sohbet kapandığında listeyi yenileyerek okunmuş/okunmamış durumunu güncelleyebiliriz.
        fetchUserConversations(); 
    };
    
    if (isLoading) {
        return <div className="loading-container">Gelen Kutunuz Yükleniyor...</div>;
    }

    if (error) {
        return <div className="error-container">{error}</div>;
    }

    return (
        <div className="user-messages-page-container">
            <header className="user-messages-header">
                <button onClick={() => navigate('/home')} className="back-button">Ana Sayfaya Dön</button>
                <h1>Mesajlarım</h1>
            </header>
            <main className="conversations-list-user">
                {conversations.length === 0 ? (
                    <div className="no-conversations-user">
                        <p>Henüz bir mesajlaşmanız bulunmuyor.</p>
                        <p>İlan detaylarından satıcı ile mesajlaşma başlatabilirsiniz.</p>
                    </div>
                ) : (
                    conversations.map(convo => (
                        <div key={convo.conversation_id} className="conversation-item-user" onClick={() => handleConversationClick(convo)}>
                            <div className="conversation-details">
                                <h3 className="vehicle-title-user">{convo.brand} {convo.model}</h3>
                                <p className="last-message-user">Son Mesaj: "{convo.message}"</p>
                                <span className="timestamp-user">
                                    {new Date(convo.created_at).toLocaleString('tr-TR', { day: '2-digit', month: 'long', hour: '2-digit', minute: '2-digit' })}
                                </span>
                            </div>
                            {convo.unread_count > 0 && (
                                <div className="unread-indicator-user" title={`${convo.unread_count} yeni mesaj`}>
                                    {convo.unread_count}
                                </div>
                            )}
                        </div>
                    ))
                )}
            </main>

            {/* Sohbet Modal'ı */}
            {selectedVehicleForChat && (
                <ChatModal
                    key={selectedVehicleForChat.id} // Key'i değiştirmek modal'ın yeniden başlatılmasını sağlar
                    vehicle={selectedVehicleForChat}
                    closeModal={closeChatModal}
                />
            )}
        </div>
    );
}

export default UserMessagesPage;
