import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import AdminChatBox from './AdminChatBox';
import './MessagesPage.css';

function MessagesPage() {
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchConversations = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Yetkilendirme token\'Ä± bulunamadÄ±. LÃ¼tfen tekrar giriÅŸ yapÄ±n.');
        setIsLoading(false);
        return;
      }

      const response = await axios.get('http://localhost:5000/api/conversations', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      
      setConversations(response.data);
      setError('');
    } catch (error) {
      console.error("KonuÅŸmalar yÃ¼klenemedi:", error);
      const errorMessage = error.response?.data?.message || 'KonuÅŸmalar yÃ¼klenirken bir hata oluÅŸtu.';
      setError(errorMessage);
      
      if (error.response?.status === 403 || error.response?.status === 401) {
        localStorage.removeItem('token');
        // Ä°steÄŸe baÄŸlÄ±: kullanÄ±cÄ±yÄ± otomatik olarak login sayfasÄ±na yÃ¶nlendirebilirsiniz.
        // window.location.href = '/';
      }
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchConversations();
    const interval = setInterval(fetchConversations, 30000); // 30 saniyede bir yenile
    return () => clearInterval(interval);
  }, [fetchConversations]);

  const refreshConversations = () => {
    setIsLoading(true);
    fetchConversations();
  };

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Gelen Kutusu YÃ¼kleniyor...</div>
      </div>
    );
  }

  if (error && conversations.length === 0) {
    return (
      <div className="error-container">
        <p>Hata: {error}</p>
        <button onClick={refreshConversations}>Tekrar Dene</button>
      </div>
    );
  }

  return (
    <div className="messages-page-container">
      <div className="conversations-sidebar">
        <div className="sidebar-header">
          <h2>Gelen Kutusu</h2>
          <button onClick={refreshConversations} className="refresh-btn" title="Yenile">
            ðŸ”„
          </button>
        </div>
        
        <div className="conversations-list">
          {conversations.length === 0 ? (
            <div className="no-conversations">
              <p>HenÃ¼z bir gÃ¶rÃ¼ÅŸme baÅŸlatÄ±lmamÄ±ÅŸ.</p>
            </div>
          ) : (
            conversations.map((convo) => (
              <div 
                key={convo.conversation_id} 
                className={`conversation-item ${
                  selectedConversation?.conversation_id === convo.conversation_id ? 'active' : ''
                }`}
                onClick={() => setSelectedConversation(convo)}
              >
                <div className="conversation-avatar">
                  {convo.user_name?.charAt(0).toUpperCase() || '?'}
                </div>
                
                <div className="conversation-content">
                  <div className="conversation-header">
                    <h4>{convo.user_name || 'Bilinmeyen KullanÄ±cÄ±'}</h4>
                    <span className="conversation-time">
                      {new Date(convo.created_at).toLocaleString('tr-TR', {
                        hour: '2-digit', minute: '2-digit'
                      })}
                    </span>
                  </div>
                  
                  <div className="vehicle-info">
                    <span className="vehicle-name">{convo.brand} {convo.model}</span>
                  </div>
                  
                  <p className="last-message">{convo.message}</p>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      <div className="chat-area">
        {selectedConversation ? (
          <AdminChatBox 
            key={selectedConversation.conversation_id} // Key deÄŸiÅŸtiÄŸinde component yeniden oluÅŸturulur
            conversationId={selectedConversation.conversation_id}
            // DiÄŸer proplara artÄ±k gerek yok, her ÅŸey conversationId ile hallediliyor.
          />
        ) : (
          <div className="no-chat-selected">
            <div className="no-chat-content">
              <h3>HoÅŸ Geldiniz!</h3>
              <p>MesajlarÄ± gÃ¶rÃ¼ntÃ¼lemek iÃ§in sol menÃ¼den bir konuÅŸma seÃ§in.</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default MessagesPage;
