import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // <-- Gerekli import eklendi
import AdminChatBox from './AdminChatBox';
import './MessagesPage.css';
import { socket } from './socket'; // <-- Merkezi socket'i kullanƒ±yoruz

function MessagesPage() {
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate(); // <-- useNavigate hook'unu kullanƒ±ma alƒ±yoruz

  const fetchConversations = useCallback(async () => {
    // Bu fonksiyonun i√ßeriƒüi aynƒ± kalƒ±yor, sadece √ßaƒüƒ±rma ≈üekli deƒüi≈üecek
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Yetkilendirme token\'ƒ± bulunamadƒ±.');
        setIsLoading(false);
        return;
      }
      const response = await axios.get('http://localhost:5000/api/conversations', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      setConversations(response.data);
      setError('');
    } catch (error) {
      console.error("Konu≈ümalar y√ºklenemedi:", error);
      setError(error.response?.data?.message || 'Konu≈ümalar y√ºklenemedi.');
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    // Socket baƒülantƒ±sƒ±nƒ± manuel olarak ba≈ülat
    socket.connect();
    
    // Verileri ilk kez y√ºkle
    fetchConversations();

    // Backend'den gelen "yenile" sinyalini dinle
    const handleRefresh = () => {
        fetchConversations();
    };
    socket.on('admin_refresh_conversations', handleRefresh);

    // Temizlik fonksiyonu: Component ekrandan kaldƒ±rƒ±ldƒ±ƒüƒ±nda √ßalƒ±≈üƒ±r
    return () => {
      socket.off('admin_refresh_conversations', handleRefresh); // Listener'ƒ± kaldƒ±r
      socket.disconnect(); // Sayfadan ayrƒ±lƒ±nca baƒülantƒ±yƒ± tamamen kapat
    };
  }, [fetchConversations]); // Bu effect sadece bir kez √ßalƒ±≈üƒ±r

  const refreshConversations = () => {
    setIsLoading(true);
    fetchConversations();
  };

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Gelen Kutusu Y√ºkleniyor...</div>
      </div>
    );
  }

  if (error && conversations.length === 0) {
    return (
      <div className="error-container">
        <p>Hata: {error}</p>
        <button onClick={refreshConversations}>Tekrar Dene</button>
      </div>
    );
  }

  return (
    <div className="messages-page-container">
      <div className="conversations-sidebar">
        <div className="sidebar-header">
          {/* === YENƒ∞: GERƒ∞ D√ñN BUTONU === 
              Bu buton kullanƒ±cƒ±yƒ± her zaman dashboard'a, yani ana panele d√∂nd√ºr√ºr */}
          <button onClick={() => navigate('/dashboard')} className="back-to-dashboard-btn" title="Y√∂netim Paneline D√∂n">
            ‚Äπ Panele D√∂n
