import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

// Bu sayfaya özel CSS'i de import edeceğiz (bir sonraki adımda oluşturacağız)
import './MyVehiclesPage.css';

function MyVehiclesPage() {
  const [vehicles, setVehicles] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMyVehicles = async () => {
      // Tarayıcı hafızasından token'ı al
      const token = localStorage.getItem('token');

      if (!token) {
        setError("Lütfen giriş yapın.");
        setIsLoading(false);
        navigate('/'); // Token yoksa login'e at
        return;
      }

      try {
        // Backend'e token ile birlikte istek at
        const response = await axios.get('http://localhost:5000/api/my-vehicles', {
          headers: {
            // Backend'deki authenticateToken middleware'inin beklediği format
            'Authorization': `Bearer ${token}`
          }
        });
        setVehicles(response.data);
      } catch (err) {
        setError('Araçlar yüklenirken bir hata oluştu.');
        console.error(err);
      } finally {
        setIsLoading(false);
      }
    };

    fetchMyVehicles();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  if (isLoading) {
    return <div className="loading-container">Yükleniyor...</div>;
  }

  if (error) {
    return <div className="error-container">{error}</div>;
  }

  return (
    <div className="my-vehicles-page">
      <header className="my-vehicles-header">
        <h1>Araçlarım</h1>
        <div>
          <button onClick={() => navigate('/user-dashboard')} className="header-btn">Ana Sayfa</button>
          <button onClick={handleLogout} className="header-btn logout">Çıkış Yap</button>
        </div>
      </header>
      <main className="vehicles-grid">
        {vehicles.length === 0 ? (
          <p className="no-vehicles-message">Henüz hiç araç eklemediniz.</p>
        ) : (
          vehicles.map(vehicle => (
            <div key={vehicle.id} className="vehicle-card">
              <img 
                // Fotoğraf yolunu backend adresine göre düzenliyoruz
                src={`http://localhost:5000/${vehicle.photo_url}`} 
                alt={`${vehicle.brand} ${vehicle.model}`} 
                className="vehicle-photo"
                // Resim yüklenemezse varsayılan bir resim göster
                onError={(e) => { e.target.onerror = null; e.target.src='https://via.placeholder.com/300x200?text=Resim+Yok'; }}
              />
              <div className="vehicle-info">
                <h3>{vehicle.brand} {vehicle.model}</h3>
                <p>{vehicle.year} | {vehicle.fuel}</p>
                <div className="price-tag">{new Intl.NumberFormat('tr-TR', { style: 'currency', currency: 'TRY' }).format(vehicle.sale_price)}</div>
              </div>
            </div>
          ))
        )}
      </main>
    </div>
  );
}

export default MyVehiclesPage;
