import React, { useState, useEffect, useMemo } from 'react'; // useMemo'yu import ediyoruz
import axios from 'axios';
import './VehiclesPage.css';

function VehiclesPage() {

  const [vehicles, setVehicles] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentVehicleId, setCurrentVehicleId] = useState(null);

  // --- YENİ: Filtreleme için state'ler ---
  const [filter, setFilter] = useState(''); // Genel arama için
  const [brandFilter, setBrandFilter] = useState('');
  const [modelFilter, setModelFilter] = useState('');
  // ------------------------------------

  const [formData, setFormData] = useState({
    brand: '', model: '', year: '', color: '', gear: '', fuel: '',
    mileage: '', purchase_price: '', sale_price: '', description: ''
  });


  const fetchVehicles = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/vehicles');
      setVehicles(response.data);
    } catch (error) {
      console.error("Araçlar çekilirken hata oluştu:", error);
    }
  };

  useEffect(() => {
    fetchVehicles();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm("Bu aracı silmek istediğinizden emin misiniz?")) {
      try {
        await axios.delete(`http://localhost:5000/api/vehicles/${id}`);
        alert("Araç başarıyla silindi.");
        fetchVehicles();
      } catch (error) {
        console.error("Araç silinirken hata oluştu:", error);
        alert("Araç silinirken bir hata oluştu.");
      }
    }
  };

  const handleEdit = (vehicle) => {
    setIsEditing(true);
    setCurrentVehicleId(vehicle.id);
    setFormData(vehicle);
    window.scrollTo(0, 0);
  };

  const cancelEdit = () => {
    setIsEditing(false);
    setCurrentVehicleId(null);
    setFormData({
      brand: '', model: '', year: '', color: '', gear: '', fuel: '',
      mileage: '', purchase_price: '', sale_price: '', description: ''
    });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (isEditing) {
      try {
        await axios.put(`http://localhost:5000/api/vehicles/${currentVehicleId}`, formData);
        alert('Araç başarıyla güncellendi!');
        cancelEdit();
        fetchVehicles();
      } catch (error) {
        console.error("Araç güncellenirken hata oluştu:", error);
        alert('Araç güncellenirken bir hata oluştu.');
      }
    } else {
      try {
        await axios.post('http://localhost:5000/api/vehicles', formData);
        alert('Araç başarıyla eklendi!');
        cancelEdit();
        fetchVehicles();
      } catch (error) {
        console.error("Araç eklenirken hata oluştu:", error);
        alert('Araç eklenirken bir hata oluştu.');
      }
    }
  };

  // --- YENİ: Filtrelenmiş araç listesi ---
  // useMemo kullanarak her render'da yeniden hesaplama yapılmasını engelliyoruz.
  // Sadece 'vehicles', 'filter', 'brandFilter' veya 'modelFilter' değiştiğinde çalışır.
  const filteredVehicles = useMemo(() => {
    return vehicles.filter(vehicle => {
      const searchTerm = filter.toLowerCase();
      // Genel arama: Marka, model, yıl veya renk alanlarından herhangi birinde arama yapar.
      const matchesGeneralSearch = searchTerm === '' ||
        vehicle.brand.toLowerCase().includes(searchTerm) ||
        vehicle.model.toLowerCase().includes(searchTerm) ||
        vehicle.year.toString().includes(searchTerm) ||
        vehicle.color.toLowerCase().includes(searchTerm);

      // Markaya göre filtreleme
      const matchesBrand = brandFilter === '' || vehicle.brand.toLowerCase().includes(brandFilter.toLowerCase());

      // Modele göre filtreleme
      const matchesModel = modelFilter === '' || vehicle.model.toLowerCase().includes(modelFilter.toLowerCase());

      return matchesGeneralSearch && matchesBrand && matchesModel;
    });
  }, [vehicles, filter, brandFilter, modelFilter]);
  // ------------------------------------

  return (
    <div className="vehicle-page-wrapper">
      <div className="vehicle-page">
        <div className="form-container">
          <h2 className="page-title">{isEditing ? 'Aracı Düzenle' : 'Araç Kayıt'}</h2>
          <form className="vehicle-form" onSubmit={handleSubmit}>
            {/* Form içeriği aynı kalıyor */}
            <div className="form-row">
              <input type="text" name="brand" placeholder="Marka" value={formData.brand} onChange={handleInputChange} required />
              <input type="text" name="model" placeholder="Model" value={formData.model} onChange={handleInputChange} required />
              <input type="number" name="year" placeholder="Yıl" value={formData.year} onChange={handleInputChange} required />
            </div>
            <div className="form-row">
              <input type="text" name="color" placeholder="Renk" value={formData.color} onChange={handleInputChange} required />
              <input type="text" name="gear" placeholder="Vites" value={formData.gear} onChange={handleInputChange} required />
              <input type="text" name="fuel" placeholder="Yakıt" value={formData.fuel} onChange={handleInputChange} required />
            </div>
            <div className="form-row">
              <input type="number" name="mileage" placeholder="Kilometre" value={formData.mileage} onChange={handleInputChange} required />
              <input type="number" name="purchase_price" placeholder="Alış Fiyatı" value={formData.purchase_price} onChange={handleInputChange} required />
              <input type="number" name="sale_price" placeholder="Satış Fiyatı" value={formData.sale_price} onChange={handleInputChange} required />
            </div>
            <div className="form-row">
              <textarea name="description" placeholder="Açıklama" value={formData.description} onChange={handleInputChange}></textarea>
            </div>
            <div className="form-buttons-container">
              <button type="submit" className="add-vehicle-btn">
                {isEditing ? 'Aracı Güncelle' : 'Araç Ekle'}
              </button>
              {isEditing && (
                <button type="button" className="cancel-edit-btn" onClick={cancelEdit}>
                  İptal
                </button>
              )}
            </div>
          </form>
        </div>

        <div className="card table-card">
          <h2>Kayıtlı Araçlar</h2>

          {/* --- YENİ: Filtreleme Arayüzü --- */}
          <div className="filter-container" style={{ display: 'flex', gap: '10px', marginBottom: '20px', padding: '10px', border: '1px solid #ddd', borderRadius: '8px' }}>
            <input
              type="text"
              placeholder="Genel Arama (Marka, Model, Yıl...)"
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
              style={{ flex: 1, padding: '8px' }}
            />
             <input
              type="text"
              placeholder="Markaya Göre Filtrele"
              value={brandFilter}
              onChange={(e) => setBrandFilter(e.target.value)}
              style={{ flex: 1, padding: '8px' }}
            />
             <input
              type="text"
              placeholder="Modele Göre Filtrele"
              value={modelFilter}
              onChange={(e) => setModelFilter(e.target.value)}
              style={{ flex: 1, padding: '8px' }}
            />
          </div>
          {/* ------------------------------ */}

          <div className="table-wrapper">
            <table className="vehicle-table">
              <thead>
                <tr>
                  <th>Marka</th><th>Model</th><th>Yıl</th><th>Renk</th><th>Vites</th><th>Yakıt</th><th>KM</th><th>Alış</th><th>Satış</th><th>Açıklama</th><th>İşlemler</th>
                </tr>
              </thead>
              <tbody>
                {/* --- DEĞİŞİKLİK: 'vehicles' yerine 'filteredVehicles' kullanılıyor --- */}
                {filteredVehicles.map((vehicle) => (
                  <tr key={vehicle.id}>
                    <td>{vehicle.brand}</td>
                    <td>{vehicle.model}</td>
                    <td>{vehicle.year}</td>
                    <td>{vehicle.color}</td>
                    <td>{vehicle.gear}</td>
                    <td>{vehicle.fuel}</td>
                    <td>{vehicle.mileage.toLocaleString('tr-TR')}</td>
                    <td className="price-cell">{parseFloat(vehicle.purchase_price).toLocaleString('tr-TR', { style: 'currency', currency: 'TRY' })}</td>
                    <td className="price-cell">{parseFloat(vehicle.sale_price).toLocaleString('tr-TR', { style: 'currency', currency: 'TRY' })}</td>
                    <td>{vehicle.description}</td>
                    <td>
                      <div className="action-buttons">
                        <button className="edit-btn" onClick={() => handleEdit(vehicle)}>Düzenle</button>
                        <button className="delete-btn" onClick={() => handleDelete(vehicle.id)}>Sil</button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            {/* --- YENİ: Filtre sonucunda araç bulunamazsa mesaj gösterme --- */}
            {filteredVehicles.length === 0 && (
                <p style={{ textAlign: 'center', padding: '20px' }}>Filtre kriterlerinize uygun araç bulunamadı.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default VehiclesPage;
