import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';

// Koruyucumuzu import ediyoruz
import ProtectedRoute from './ProtectedRoute'; // Dosyanın doğru yolda olduğundan emin olun

// Sayfalarımızı import ediyoruz
import LoginPage from './LoginPage';
import ForgotPasswordPage from './ForgotPasswordPage';
import Dashboard from './Dashboard';
import VehiclesPage from './VehiclesPage';
import PersonnelPage from './PersonnelPage';
import CreditCalculatorPage from './CreditCalculatorPage';
import MarketValuePage from './MarketValuePage';

function App() {
  return (
    <Router>
      <Routes>
        {/* ======================================= */}
        {/*      HERKESE AÇIK SAYFALAR              */}
        {/* ======================================= */}
        {/* path="/" login sayfasına yönlendirdiği için korunmasına gerek yok. */}
        <Route path="/" element={<LoginPage />} />
        <Route path="/forgot-password" element={<ForgotPasswordPage />} />
        {/* Eğer login component'iniz "/" yolunda değil de "/login" yolundaysa, yukarıdaki satırı düzenleyin. */}
        {/* Örnek: <Route path="/login" element={<LoginPage />} /> */}


        {/* ======================================= */}
        {/*      KORUMA ALTINDAKİ SAYFALAR          */}
        {/* ======================================= */}
        {/* Artık bu sayfalara giriş yapmadan URL ile erişilemez. */}

        <Route 
          path="/dashboard" 
          element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          } 
        />
        <Route 
          path="/vehicles" 
          element={
            <ProtectedRoute>
              <VehiclesPage />
            </ProtectedRoute>
          } 
        />
        <Route 
          path="/personnel" 
          element={
            <ProtectedRoute>
              <PersonnelPage />
            </ProtectedRoute>
          } 
        />
        <Route 
          path="/kredi" 
          element={
            <ProtectedRoute>
              <CreditCalculatorPage />
            </ProtectedRoute>
          } 
        />
        <Route 
          path="/piyasa" 
          element={
            <ProtectedRoute>
              <MarketValuePage />
            </ProtectedRoute>
          } 
        />
      </Routes>
    </Router>
  );
}

export default App;
