import React, { useEffect, useState } from 'react';
import axios from 'axios';

function VehiclesPage() {
  const [brand, setBrand] = useState('');
  const [model, setModel] = useState('');
  const [year, setYear] = useState('');
  const [price, setPrice] = useState('');
  const [description, setDescription] = useState('');
  const [vehicles, setVehicles] = useState([]);
  const [message, setMessage] = useState('');

  const fetchVehicles = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/vehicles');
      setVehicles(res.data);
    } catch (err) {
      console.error('Araçlar alınamadı', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/vehicles', {
        brand, model, year, price, description
      });
      setMessage('Araç başarıyla eklendi.');
      setBrand('');
      setModel('');
      setYear('');
      setPrice('');
      setDescription('');
      fetchVehicles();
    } catch (err) {
      setMessage('Araç eklenirken hata oluştu.');
    }
  };

  useEffect(() => {
    fetchVehicles();
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Araç Kayıt</h2>
      <form onSubmit={handleSubmit} style={{ marginBottom: '2rem' }}>
        <input type="text" placeholder="Marka" value={brand} onChange={(e) => setBrand(e.target.value)} required />
        <input type="text" placeholder="Model" value={model} onChange={(e) => setModel(e.target.value)} required />
        <input type="number" placeholder="Yıl" value={year} onChange={(e) => setYear(e.target.value)} required />
        <input type="number" placeholder="Fiyat" value={price} onChange={(e) => setPrice(e.target.value)} required />
        <textarea placeholder="Açıklama" value={description} onChange={(e) => setDescription(e.target.value)} />
        <button type="submit">Araç Ekle</button>
      </form>

      {message && <p>{message}</p>}

      <h3>Kayıtlı Araçlar</h3>
      <table border="1" cellPadding="10" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Marka</th>
            <th>Model</th>
            <th>Yıl</th>
            <th>Fiyat</th>
            <th>Açıklama</th>
          </tr>
        </thead>
        <tbody>
          {vehicles.map(vehicle => (
            <tr key={vehicle.id}>
              <td>{vehicle.brand}</td>
              <td>{vehicle.model}</td>
              <td>{vehicle.year}</td>
              <td>{vehicle.price} ₺</td>
              <td>{vehicle.description}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default VehiclesPage;
