const handleSubmit = async (e) => {
  e.preventDefault();

  const data = {
    brand,
    model,
    year,
    price,
    description,
  };

  try {
    if (editId) {
      // Güncelleme modu
      await axios.put(`http://localhost:5000/api/vehicles/${editId}`, data);
      setMessage('Araç güncellendi.');
    } else {
      // Yeni kayıt modu
      await axios.post('http://localhost:5000/api/vehicles', data);
      setMessage('Araç başarıyla eklendi.');
    }

    clearForm();
    fetchVehicles();
  } catch (err) {
    console.error(err);
    setMessage('İşlem sırasında hata oluştu.');
  }
};
