// src/LoginPage.js
// ===================================
import React, { useState } from 'react';
import axios from 'axios';
import './LoginPage.css';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

function LoginPage() {
  // Yönetici mi, kullanıcı mı? Artık sadece bu state'e ihtiyacımız var.
  const [loginMode, setLoginMode] = useState('admin'); 
  
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('http://localhost:5000/api/login', { email, password });
      const token = res.data.token;
      localStorage.setItem('token', token);
      
      const decodedToken = jwtDecode(token);
      
      if (decodedToken.role === 'admin') {
        navigate('/dashboard', { replace: true });
      } else {
        // App.js'deki rotamız /home olduğu için burayı /home yapıyoruz.
        navigate('/home', { replace: true });
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Giriş işlemi başarısız');
    }
  };

  // Bu sayfa artık sadece giriş yapmak için var. Kayıt ve diğer formlar için
  // aşağıdaki butonlar kendi sayfalarına yönlendiriyor.
  return (
    <div className="login-container">
      <div className="login-wrapper">
        <div className="role-tabs">
          <button 
            className={`tab-button ${loginMode === 'admin' ? 'active' : ''}`}
            onClick={() => setLoginMode('admin')}
          >
            Yönetici Girişi
          </button>
          <button 
            className={`tab-button ${loginMode === 'user' ? 'active' : ''}`}
            onClick={() => setLoginMode('user')}
          >
            Kullanıcı Girişi
          </button>
        </div>

        {/* Formun onSubmit olayı artık her zaman handleLogin'i çalıştıracak. */}
        <form className="login-form" onSubmit={handleLogin}>
          {/* Başlık, seçilen sekmeye göre değişecek */}
          <h2>{loginMode === 'admin' ? 'Yönetici Girişi' : 'Kullanıcı Girişi'}</h2>
          <input
            type="email"
            placeholder="E-posta Adresi"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Şifre"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">
            Giriş Yap
          </button>
          {error && <p className="error">{error}</p>}
          
          {/* Kaydol ve Şifremi Unuttum butonları artık her zaman görünür */}
          <div className="form-switch">
            <button
              type="button"
              onClick={() => alert("Kayıt olma özelliği yakında eklenecektir.")}
            >
              Kaydol
            </button>
            <button type="button" onClick={() => navigate('/forgot-password')}>
              Şifremi Unuttum
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
