import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { jwtDecode } from 'jwt-decode';
import './AdminChatBox.css';

const socket = io('http://localhost:5000');

function AdminChatBox({ conversationId }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [adminUser, setAdminUser] = useState(null);
  const [conversationInfo, setConversationInfo] = useState({});
  const messagesEndRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decoded = jwtDecode(token);
      setAdminUser(decoded);
    }
  }, []);

  useEffect(() => {
    if (conversationId) {
      // conversationId'den bilgileri ayıkla
      const parts = conversationId.split('-');
      const userId = parts[0].split('_')[1];
      const vehicleId = parts[2] ? parts[2].split('_')[1] : null;
      setConversationInfo({ userId, vehicleId });

      // Önceki event listener'ları temizle
      socket.off('load_messages');
      socket.off('receive_message');

      socket.emit('join_room', conversationId);
      
      socket.on('load_messages', (loadedMessages) => {
        setMessages(loadedMessages);
      });
      
      socket.on('receive_message', (message) => {
        setMessages((prev) => [...prev, message]);
      });

      return () => {
        socket.off('load_messages');
        socket.off('receive_message');
      };
    }
  }, [conversationId]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim() === '' || !adminUser || !conversationInfo.userId) return;
    
    const messageData = {
      conversation_id: conversationId,
      sender_id: adminUser.id,
      receiver_id: parseInt(conversationInfo.userId),
      vehicle_id: conversationInfo.vehicleId ? parseInt(conversationInfo.vehicleId) : null,
      message: newMessage.trim(),
    };

    socket.emit('send_message', messageData);
    setNewMessage('');
  };

  if (!conversationId) {
    return (
      <div className="admin-chat-box-empty">
        <p>Görüntülemek için bir konuşma seçin.</p>
      </div>
    );
  }

  return (
    <div className="admin-chat-box">
      <div className="chat-messages-admin">
        {messages.map((msg, index) => (
          <div
            key={msg.id || index}
            className={`message-bubble-admin ${parseInt(msg.sender_id) === parseInt(adminUser?.id) ? 'sent-admin' : 'received-admin'}`}
          >
            <p>{msg.message}</p>
            <span className="message-time-admin">
              {new Date(msg.created_at).toLocaleTimeString('tr-TR', { hour: '2-digit', minute: '2-digit' })}
            </span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <form className="chat-input-form-admin" onSubmit={handleSendMessage}>
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Cevabınızı yazın..."
        />
        <button type="submit" disabled={!newMessage.trim()}>➢</button>
      </form>
    </div>
  );
}

export default AdminChatBox;
