// BU KODUN TAMAMINI KOPYALAYIP MEVCUT ChatModal.js DOSYANIZLA DEĞİŞTİRİN

import React, { useState, useEffect, useRef } from 'react';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';
import './ChatModal.css';
import { socket } from './socket';

function ChatModal({ vehicle, closeModal }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [currentUser, setCurrentUser] = useState(null);
  const messagesEndRef = useRef(null);

  // Bu useEffect, sadece modal ilk açıldığında çalışarak kullanıcıyı belirler.
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        setCurrentUser(jwtDecode(token));
      } catch (e) {
        console.error("Geçersiz Token, sohbet kapatılıyor:", e);
        closeModal();
      }
    } else {
      console.error("Token bulunamadı, sohbet kapatılıyor.");
      closeModal();
    }
  }, [closeModal]); // closeModal referansı değişmediği sürece tekrar çalışmaz.

  // *** GÜNCELLEME: Sohbet kimliği artık araçtan bağımsız, sadece kullanıcıya özel. ***
  const conversationId = currentUser ? `user_${currentUser.id}-admin_1` : null;

  // Bu useEffect, socket bağlantısını ve olay dinleyicilerini yönetir.
  // currentUser veya conversationId değiştiğinde yeniden kurulur.
  useEffect(() => {
    // Gerekli bilgiler olmadan bağlantı kurmayı deneme.
    if (!currentUser || !conversationId) {
      return;
    }

    if (!socket.connected) {
      socket.connect();
    }

    function onConnect() {
      socket.emit('join_room', conversationId);
    }
    
    function handleLoadMessages(loadedMessages) {
      setMessages(loadedMessages);
    }

    function handleReceiveMessage(message) {
      if (message.conversation_id === conversationId) {
        setMessages((prev) => [...prev, message]);
      }
    }

    function handleMessageDeleted({ messageId }) {
      setMessages((prevMessages) => prevMessages.filter(msg => msg.id !== messageId));
    }

    socket.on('connect', onConnect);
    socket.on('load_messages', handleLoadMessages);
    socket.on('receive_message', handleReceiveMessage);
    socket.on('message_deleted', handleMessageDeleted);
    
    if (socket.connected) {
      onConnect();
    }

    // Temizleme fonksiyonu: Modal kapandığında dinleyicileri kaldır.
    return () => {
      socket.off('connect', onConnect);
      socket.off('load_messages', handleLoadMessages);
      socket.off('receive_message', handleReceiveMessage);
      socket.off('message_deleted', handleMessageDeleted);
      // Not: disconnect() burada çağrılmamalı, çünkü kullanıcı başka bir araç için sohbet açabilir.
      // Bağlantı App.js'de veya çıkış yapıldığında yönetilmelidir.
    };
  }, [currentUser, conversationId]); // Bağımlılık dizisi düzeltildi.

  // Mesajlar her değiştiğinde en sona kaydır.
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    const trimmedMessage = newMessage.trim();
    if (trimmedMessage === '' || !currentUser || !conversationId) return;

    const messageData = {
      // *** GÜNCELLEME: Mesaj gönderilirken de yeni conversationId kullanılır. ***
      conversation_id: conversationId,
      sender_id: currentUser.id,
      receiver_id: 1, // Admin ID'si (varsayım)
      vehicle_id: vehicle.id, // Hangi araç hakkında konuşulduğunu belirtmek için bu bilgi korunur.
      message: trimmedMessage,
    };

    socket.emit('send_message', messageData);
    setNewMessage('');
  };

  const handleDeleteMessage = async (messageId) => {
    if (window.confirm("Mesajı silmek istediğinizden emin misiniz?")) {
        try {
            const token = localStorage.getItem('token');
            await axios.delete(`http://localhost:5000/api/messages/${messageId}`, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
        } catch (error) {
            console.error("Mesaj silinemedi:", error);
            alert(error.response?.data?.message || "Mesaj silinirken bir hata oluştu.");
        }
    }
  };
  
  return (
    <div className="chat-modal-overlay" onClick={closeModal}>
      <div className="chat-modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="chat-header">
          <h3>{vehicle.brand} {vehicle.model}</h3>
          <span>Satıcı ile Mesajlaşma</span>
          <button onClick={closeModal} className="close-chat-btn">×</button>
        </div>
        <div className="chat-messages">
          {messages.map((msg) => {
            // currentUser null değilse ID'sine bak, null ise false kabul et.
            const isSentByUser = currentUser ? parseInt(msg.sender_id) === currentUser.id : false;
            return (
              <div
                key={msg.id}
                className={`message-wrapper ${isSentByUser ? 'sent' : 'received'}`}
              >
                <div className="message-bubble">
                  {isSentByUser && (
                    <button 
                      className="delete-message-btn" 
                      onClick={() => handleDeleteMessage(msg.id)}
                      title="Mesajı Sil"
                    >
                      ×
                    </button>
                  )}
                  <p>{msg.message}</p>
                  <span className="message-time">
                    {new Date(msg.created_at).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                  </span>
                </div>
              </div>
            );
          })}
          <div ref={messagesEndRef} />
        </div>
        <form className="chat-input-form" onSubmit={handleSendMessage}>
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Mesajınızı yazın..."
            disabled={!currentUser}
          />
          <button type="submit" disabled={!currentUser || !newMessage.trim()}>Gönder</button>
        </form>
      </div>
    </div>
  );
}

export default ChatModal;
