import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './PersonnelPage.css'; // Stil dosyamızı import ediyoruz

function PersonnelPage() {
    const [personnelList, setPersonnelList] = useState([]);
    const [isEditing, setIsEditing] = useState(false);
    const [currentPersonnelId, setCurrentPersonnelId] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');

    const initialFormState = {
        ad: '', soyad: '', tc_kimlik: '', telefon: '', adres: '',
        pozisyon: '', maas: '', ise_baslama_tarihi: ''
    };
    const [formData, setFormData] = useState(initialFormState);

    const fetchPersonnel = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/personnel');
            setPersonnelList(response.data);
        } catch (error) {
            console.error("Personel listesi çekilirken hata oluştu:", error);
        }
    };

    useEffect(() => {
        fetchPersonnel();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (isEditing) {
                await axios.put(`http://localhost:5000/api/personnel/${currentPersonnelId}`, formData);
                alert('Personel başarıyla güncellendi!');
            } else {
                await axios.post('http://localhost:5000/api/personnel', formData);
                alert('Personel başarıyla eklendi!');
            }
            cancelEdit();
            fetchPersonnel();
        } catch (error) {
            console.error("İşlem sırasında hata:", error);
            alert(error.response?.data?.message || 'Bir hata oluştu.');
        }
    };

    const handleEdit = (person) => {
        setIsEditing(true);
        setCurrentPersonnelId(person.id);
        // Tarih formatını YYYY-MM-DD'ye çevirerek forma yerleştir
        const formattedPerson = {
            ...person,
            ise_baslama_tarihi: new Date(person.ise_baslama_tarihi).toISOString().split('T')[0]
        };
        setFormData(formattedPerson);
        window.scrollTo(0, 0);
    };

    const handleDelete = async (id) => {
        if (window.confirm("Bu personeli silmek istediğinizden emin misiniz?")) {
            try {
                await axios.delete(`http://localhost:5000/api/personnel/${id}`);
                alert("Personel başarıyla silindi.");
                fetchPersonnel();
            } catch (error) {
                console.error("Silme hatası:", error);
            }
        }
    };

    const cancelEdit = () => {
        setIsEditing(false);
        setCurrentPersonnelId(null);
        setFormData(initialFormState);
    };

    const filteredPersonnel = personnelList.filter(p => 
        p.ad.toLowerCase().includes(searchTerm.toLowerCase()) ||
        p.soyad.toLowerCase().includes(searchTerm.toLowerCase()) ||
        p.tc_kimlik.includes(searchTerm)
    );

    return (
        <div className="personnel-page-wrapper">
            <div className="personnel-page">
                <div className="card form-card">
                    <h2>{isEditing ? 'Personel Bilgilerini Düzenle' : 'Personel Kayıt'}</h2>
                    <form onSubmit={handleSubmit}>
                        <div className="form-grid">
                            <input name="ad" value={formData.ad} onChange={handleInputChange} placeholder="Ad" required />
                            <input name="soyad" value={formData.soyad} onChange={handleInputChange} placeholder="Soyad" required />
                            <input name="tc_kimlik" value={formData.tc_kimlik} onChange={handleInputChange} placeholder="TC Kimlik No" required maxLength="11" />
                            <input name="telefon" value={formData.telefon} onChange={handleInputChange} placeholder="Telefon" required />
                            <input name="pozisyon" value={formData.pozisyon} onChange={handleInputChange} placeholder="Pozisyon" />
                            <input name="maas" type="number" value={formData.maas} onChange={handleInputChange} placeholder="Maaş" />
                            <input name="ise_baslama_tarihi" type="date" value={formData.ise_baslama_tarihi} onChange={handleInputChange} required />
                            <textarea name="adres" value={formData.adres} onChange={handleInputChange} placeholder="Adres" className="address-area"></textarea>
                        </div>
                        <div className="form-buttons">
                            <button type="submit" className="submit-btn">{isEditing ? 'Güncelle' : 'Kaydet'}</button>
                            {isEditing && <button type="button" className="cancel-btn" onClick={cancelEdit}>İptal</button>}
                        </div>
                    </form>
                </div>

                <div className="card table-card">
                    <div className="table-header">
                        <h2>Kayıtlı Personeller</h2>
                        <input 
                            type="text" 
                            placeholder="Filtrele (Ad, Soyad, TC...)" 
                            className="filter-input"
                            value={searchTerm}
                            onChange={e => setSearchTerm(e.target.value)}
                        />
                    </div>
                    <div className="table-wrapper">
                        <table>
                            <thead>
                                <tr>
                                    <th>Ad Soyad</th><th>TC Kimlik</th><th>Telefon</th><th>Pozisyon</th><th>İşe Başlama</th><th>İşlemler</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filteredPersonnel.map(person => (
                                    <tr key={person.id}>
                                        <td>{person.ad} {person.soyad}</td>
                                        <td>{person.tc_kimlik}</td>
                                        <td>{person.telefon}</td>
                                        <td>{person.pozisyon}</td>
                                        <td>{new Date(person.ise_baslama_tarihi).toLocaleDateString('tr-TR')}</td>
                                        <td>
                                            <div className="action-buttons">
                                                <button className="edit-btn" onClick={() => handleEdit(person)}>Düzenle</button>
                                                <button className="delete-btn" onClick={() => handleDelete(person.id)}>Sil</button>
                                            </div>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default PersonnelPage;
