import React, { useState } from 'react';
import './MarketValuePage.css'; // Stil dosyamızı import ediyoruz.

function MarketValuePage() {
    // Tüm filtrelerimizi tek bir state içinde yönetiyoruz.
    const [filters, setFilters] = useState({
        brand: '',
        model: '',
        yearMin: '',
        yearMax: '',
        kmMin: '',
        kmMax: '',
        gear: '', // Boş değer, 'Tümü' anlamına gelecek
        fuel: '', // Boş değer, 'Tümü' anlamına gelecek
    });

    // Formdaki herhangi bir input değiştiğinde state'i güncelleyen fonksiyon.
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFilters(prev => ({ ...prev, [name]: value }));
    };

    // Metinleri URL uyumlu hale getiren yardımcı fonksiyon (ör: "Opel Astra" -> "opel-astra")
    const formatForUrl = (text) => text.trim().toLowerCase().replace(/\s+/g, '-');

    // Sahibinden.com için URL oluşturan ve yönlendiren fonksiyon
    const redirectToSahibinden = () => {
        const baseDomain = 'https://www.sahibinden.com';
        const pathParts = ['otomobil'];

        // Marka ve Model'i URL yoluna ekliyoruz.
        if (filters.brand) {
            let vehiclePath = formatForUrl(filters.brand);
            if (filters.model) {
                vehiclePath += `-${formatForUrl(filters.model)}`;
            }
            pathParts.push(vehiclePath);
        }

        // Yakıt tipini URL yoluna ekliyoruz (Sahibinden bu şekilde çalışıyor).
        if (filters.fuel) {
            pathParts.push(filters.fuel);
        }

        // Vites tipini URL yoluna ekliyoruz.
        if (filters.gear) {
            pathParts.push(filters.gear);
        }

        // Diğer filtreleri URL parametresi olarak hazırlıyoruz (? sonrası).
        const params = new URLSearchParams();
        if (filters.yearMin) params.append('a5_min', filters.yearMin);
        if (filters.yearMax) params.append('a5_max', filters.yearMax);
        if (filters.kmMin) params.append('a4_min', filters.kmMin);
        if (filters.kmMax) params.append('a4_max', filters.kmMax);
        
        // Tüm parçaları birleştirerek nihai URL'i oluşturuyoruz.
        const finalPath = pathParts.join('/');
        let finalUrl = `${baseDomain}/${finalPath}`;
        if (params.toString()) {
            finalUrl += `?${params.toString()}`;
        }

        console.log("Sahibinden URL:", finalUrl);
        window.open(finalUrl, '_blank');
    };

    // Arabam.com için URL oluşturan ve yönlendiren fonksiyon
    const redirectToArabamCom = () => {
        let baseUrl = 'https://www.arabam.com/ikinci-el/otomobil';

        // Marka ve Model'i URL yoluna ekliyoruz.
        if (filters.brand) {
            baseUrl += `/${formatForUrl(filters.brand)}`;
            if (filters.model) {
                baseUrl += `-${formatForUrl(filters.model)}`;
            }
        }
        
        // Arabam.com'da TÜM filtreler parametre olarak ekleniyor.
        const params = new URLSearchParams();
        if (filters.yearMin) params.append('minYear', filters.yearMin);
        if (filters.yearMax) params.append('maxYear', filters.yearMax);
        if (filters.kmMin) params.append('minKm', filters.kmMin);
        if (filters.kmMax) params.append('maxKm', filters.kmMax);
        if (filters.fuel) params.append('fuel', filters.fuel); // Arabam.com'da metin yerine sayısal ID'ler beklenir
        if (filters.gear) params.append('gear', filters.gear); // ama birçok metinsel değeri de kabul ediyorlar.
        
        let finalUrl = baseUrl;
        if (params.toString()) {
            finalUrl += `?${params.toString()}`;
        }

        console.log("Arabam.com URL:", finalUrl);
        window.open(finalUrl, '_blank');
    };


    return (
        <div className="comparison-wrapper">
            <div className="comparison-card">
                <h2>Araç Piyasa Değeri Karşılaştırma</h2>
                <p>Filtreleri doldurarak Sahibinden.com ve Arabam.com'daki piyasa değerlerini anında görün.</p>
                
                <div className="filter-grid">
                    <div className="form-group">
                        <label>Marka</label>
                        <input name="brand" value={filters.brand} onChange={handleInputChange} placeholder="örn: Opel" />
                    </div>
                    <div className="form-group">
                        <label>Model</label>
                        <input name="model" value={filters.model} onChange={handleInputChange} placeholder="örn: Astra" />
                    </div>
                    <div className="form-group">
                        <label>Vites Tipi</label>
                        <select name="gear" value={filters.gear} onChange={handleInputChange}>
                            <option value="">Tümü</option>
                            <option value="manuel">Manuel</option>
                            <option value="otomatik">Otomatik</option>
                            <option value="yari-otomatik">Yarı Otomatik</option>
                        </select>
                    </div>
                    <div className="form-group">
                        <label>Yakıt Tipi</label>
                        <select name="fuel" value={filters.fuel} onChange={handleInputChange}>
                            <option value="">Tümü</option>
                            <option value="benzin">Benzin</option>
                            <option value="dizel">Dizel</option>
                            <option value="elektrik">Elektrik</option>
                            <option value="lpg">Benzin & LPG</option>
                        </select>
                    </div>
                    <div className="form-group">
                        <label>Yıl (En Az)</label>
                        <input type="number" name="yearMin" value={filters.yearMin} onChange={handleInputChange} placeholder="örn: 2015" />
                    </div>
                    <div className="form-group">
                        <label>Yıl (En Çok)</label>
                        <input type="number" name="yearMax" value={filters.yearMax} onChange={handleInputChange} placeholder="örn: 2020" />
                    </div>
                     <div className="form-group">
                        <label>KM (En Az)</label>
                        <input type="number" name="kmMin" value={filters.kmMin} onChange={handleInputChange} placeholder="örn: 50000" />
                    </div>
                    <div className="form-group">
                        <label>KM (En Çok)</label>
                        <input type="number" name="kmMax" value={filters.kmMax} onChange={handleInputChange} placeholder="örn: 150000" />
                    </div>
                </div>

                <div className="button-container">
                    <button className="search-button sahibinden-button" onClick={redirectToSahibinden}>
                        Sahibinden.com'da Ara
                    </button>
                    <button className="search-button arabam-button" onClick={redirectToArabamCom}>
                        Arabam.com'da Ara
                    </button>
                </div>
            </div>
        </div>
    );
}

export default MarketValuePage ;
