import React, { useState } from 'react';
import axios from 'axios';
import './CreditCalculatorPage.css'; // Stil dosyasını import et

function CreditCalculatorPage() {
    const [formVerisi, setFormVerisi] = useState({
        krediTutari: "",
        vade: "",
        aylikFaizOrani: "",
    });
    const [sonuc, setSonuc] = useState(null);
    const [yukleniyor, setYukleniyor] = useState(false);

    const handleDegisiklik = (e) => {
        const { name, value } = e.target;
        setFormVerisi(prev => ({ ...prev, [name]: value }));
    };

    const handleHesapla = async (e) => {
        e.preventDefault();
        setYukleniyor(true);
        setSonuc(null);

        try {
            const response = await axios.post('http://localhost:5000/api/kredi/hesapla', {
                krediTutari: Number(formVerisi.krediTutari),
                vade: Number(formVerisi.vade),
                aylikFaizOrani: Number(formVerisi.aylikFaizOrani),
            });
            setSonuc(response.data);
        } catch (error) {
            console.error("Hesaplama sırasında hata oluştu!", error);
            alert("Hesaplama başarısız oldu. Backend sunucunuzun çalıştığından emin olun.");
        } finally {
            setYukleniyor(false);
        }
    };

    const formatlaPara = (deger) => {
        return new Intl.NumberFormat('tr-TR', { minimumFractionDigits: 2, maximumFractionDigits: 2 }).format(deger);
    };

    return (
        <div className="kredi-hesaplama-container">
            <div className="form-alani">
                <h2>Araç Kredisi Hesaplama</h2> 
                <p>* Doldurulması zorunlu alanlar.</p>
                <form onSubmit={handleHesapla}>
                    <div className="form-grup">
                        <label>* Kredi Tutarı:</label>
                        <input type="number" name="krediTutari" value={formVerisi.krediTutari} onChange={handleDegisiklik} required />
                    </div>
                    <div className="form-grup">
                        <label>* Vade (Ay):</label>
                        <input type="number" name="vade" value={formVerisi.vade} onChange={handleDegisiklik} required />
                    </div>
                    <div className="form-grup">
                        <label>* Faiz Oranı (%):</label>
                        <input type="number" step="0.01" name="aylikFaizOrani" value={formVerisi.aylikFaizOrani} onChange={handleDegisiklik} required />
                    </div>
                    <button type="submit" disabled={yukleniyor}>
                        {yukleniyor ? 'Hesaplanıyor...' : 'Hesapla'}
                    </button>
                </form>
            </div>

            {/* ===== DEĞİŞİKLİK BURADA BAŞLIYOR ===== */}
            {/* Sonuçları ve teklifleri bir arada tutan yeni bir ana kapsayıcı ekliyoruz */}
            {sonuc && (
                <div className="sonuc-ve-teklifler">
                    <div className="sonuc-alani">
                        <h3>Hesaplama Sonuçları</h3>
                        <div className="ozet-bilgiler">
                            <div><strong>Kredi Türü:</strong> {sonuc.krediTuru}</div>
                            <div><strong>Kredi Tutarı:</strong> {formatlaPara(sonuc.krediTutari)} TL</div>
                            <div><strong>Toplam Geri Ödenecek Tutar:</strong> {formatlaPara(sonuc.toplamGeriOdeme)} TL</div>
                            <div><strong>Aylık Taksit Tutarı:</strong> {formatlaPara(sonuc.aylikTaksit)} TL</div>
                        </div>
                        
                        <table>
                            <thead>
                                <tr>
                                    <th>NO</th>
                                    <th>TARİH</th>
                                    <th>TAKSİT</th>
                                    <th>ANAPARA</th>
                                    <th>FAİZ</th>
                                    <th>KKDF</th>
                                    <th>BSMV</th>
                                    <th>KALAN ANAPARA</th>
                                </tr>
                            </thead>
                            <tbody>
                                {sonuc.odemePlani.map((taksit) => (
                                    <tr key={taksit.taksitNo}>
                                        <td>{taksit.taksitNo}</td>
                                        <td>{taksit.tarih}</td>
                                        <td>{formatlaPara(taksit.taksitTutari)} TL</td>
                                        <td>{formatlaPara(taksit.anapara)} TL</td>
                                        <td>{formatlaPara(taksit.faiz)} TL</td>
                                        <td>{formatlaPara(taksit.kkdf)} TL</td>
                                        <td>{formatlaPara(taksit.bsmv)} TL</td>
                                        <td>{formatlaPara(taksit.kalanAnapara)} TL</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>

                    {/* ===== YENİ EKLENEN ALTERNATİF TEKLİFLER BÖLÜMÜ ===== */}
                    {sonuc.alternatifTeklifler && (
                        <div className="teklifler-alani">
                            <h4>Alternatif Banka Teklifleri</h4>
                            <p>Aşağıdaki bankaların sitelerine giderek güncel teklifleri inceleyebilirsiniz.</p>
                            <div className="teklif-listesi">
                                {sonuc.alternatifTeklifler.map((teklif, index) => (
                                    <div key={index} className="teklif-karti">
                                        <img src={teklif.logoUrl} alt={`${teklif.bankaAdi} logo`} />
                                        <a 
                                            href={teklif.yonlendirmeUrl} 
                                            target="_blank" 
                                            rel="noopener noreferrer" 
                                            className="teklif-butonu"
                                        >
                                            Teklifi İncele
                                        </a>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}
                </div>
            )}
            {/* ===== DEĞİŞİKLİK BURADA BİTİYOR ===== */}
        </div>
    );
}

export default CreditCalculatorPage;
