import React, { useState, useEffect, useRef } from 'react';
import { jwtDecode } from 'jwt-decode';
import './AdminChatBox.css';
import { socket } from './socket'; // <-- Merkezi socket'i import ediyoruz

function AdminChatBox({ conversationId }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [adminUser, setAdminUser] = useState(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    // Admin bilgisini token'dan al
    const token = localStorage.getItem('token');
    if (token) {
      try {
        setAdminUser(jwtDecode(token));
      } catch (error) {
        console.error("Admin token decode error:", error);
      }
    }
  }, []);

  useEffect(() => {
    if (!conversationId || !adminUser) {
      return; // Gerekli bilgiler yoksa hi√ßbir ≈üey yapma
    }

    // 1. Manuel olarak baƒülantƒ±yƒ± ba≈ülat
    socket.connect();

    // 2. Baƒülantƒ± kurulduƒüunda odaya katƒ±l
    function onConnect() {
      console.log('Admin Socket Baƒülandƒ±! Odaya katƒ±lƒ±yor:', conversationId);
      socket.emit('join_room', conversationId);
    }

    // 3. Mesajlarƒ± dinle
    function onReceiveMessage(message) {
      if (message.conversation_id === conversationId) {
        setMessages((prev) => [...prev, message]);
      }
    }
    
    // 4. Ge√ßmi≈ü mesajlarƒ± dinle
    function onLoadMessages(loadedMessages) {
        setMessages(loadedMessages);
    }

    // Listener'larƒ± (dinleyicileri) ekle
    socket.on('connect', onConnect);
    socket.on('receive_message', onReceiveMessage);
    socket.on('load_messages', onLoadMessages);

    // ƒ∞lk baƒülantƒ± zaten kurulmu≈üsa onConnect'i manuel tetikle
    if (socket.connected) {
        onConnect();
    }

    // Component'tan √ßƒ±kƒ±ldƒ±ƒüƒ±nda (unmount) temizlik yap
    return () => {
      console.log('AdminChatBox temizleniyor, listenerlar kaldƒ±rƒ±lƒ±yor.');
      socket.off('connect', onConnect);
      socket.off('receive_message', onReceiveMessage);
      socket.off('load_messages', onLoadMessages);
      // Eƒüer ba≈üka bir sohbet kutusu a√ßƒ±k kalmayacaksa baƒülantƒ±yƒ± kapatabiliriz
      // socket.disconnect(); 
    };
  }, [conversationId, adminUser]); // Sadece bu deƒüerler deƒüi≈ütiƒüinde effect'i yeniden √ßalƒ±≈ütƒ±r

  useEffect(() => {
    // Mesaj geldiƒüinde en alta kaydƒ±r
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    const trimmedMessage = newMessage.trim();
    if (trimmedMessage === '' || !adminUser) return;

    // conversationId'den kullanƒ±cƒ± ID'sini al
    const userIdPart = conversationId.split('-').find(p => p.startsWith('user_'));
    const vehicleIdPart = conversationId.split('-').find(p => p.startsWith('vehicle_'));
    if (!userIdPart) return; // Kullanƒ±cƒ± ID'si bulunamazsa g√∂nderme

    const receiver_id = parseInt(userIdPart.split('_')[1]);
    const vehicle_id = vehicleIdPart ? parseInt(vehicleIdPart.split('_')[1]) : null;

    const messageData = {
      conversation_id: conversationId,
      sender_id: adminUser.id,
      receiver_id,
      vehicle_id,
      message: trimmedMessage,
    };

    socket.emit('send_message', messageData);
    setNewMessage('');
  };

  if (!conversationId) {
    return (
      <div className="admin-chat-box-empty">
        <p>G√∂r√ºnt√ºlemek i√ßin bir konu≈üma se√ßin.</p>
      </div>
    );
  }

  return (
    <div className="admin-chat-box">
      <div className="chat-messages-admin">
        {messages.map((msg, index) => {
          const isAdminMessage = parseInt(msg.sender_id) === adminUser?.id;
          return (
            <div key={msg.id || index} className={`message-container ${isAdminMessage ? 'admin-message' : 'user-message'}`}>
              <div className={`message-bubble ${isAdminMessage ? 'admin-bubble' : 'user-bubble'}`}>
                <p>{msg.message}</p>
                <span className="message-time">
                  {new Date(msg.created_at).toLocaleTimeString('tr-TR', { hour: '2-digit', minute: '2-digit' })}
                </span>
              </div>
            </div>
          );
        })}
        <div ref={messagesEndRef} />
      </div>
      
      <form className="chat-input-form-admin" onSubmit={handleSendMessage}>
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Cevabƒ±nƒ±zƒ± yazƒ±n..."
        />
        <button type="submit" disabled={!newMessage.trim()}>
          ‚û¢
        </button>
      </form>
    </div>
  );
}

export default AdminChatBox;
