import React, { useState } from 'react';
import './MarketValuePage.css'; // CSS dosyanızın yolunu kontrol edin.

function MarketValuePage() {
    // State'e 'series' ve 'trimPackage' alanlarını ekliyoruz.
    const [filters, setFilters] = useState({
        brand: '',
        model: '',
        series: '', // Yeni Alan: Seri (örn: 1.3 CDTI)
        trimPackage: '', // Yeni Alan: Donanım Paketi (örn: Enjoy Plus)
        yearMin: '',
        yearMax: '',
        kmMax: '',
        fuelTypes: [],
        gearTypes: [],
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFilters(prev => ({ ...prev, [name]: value }));
    };

    const handleCheckboxChange = (e) => {
        const { name, value, checked } = e.target;
        setFilters(prev => {
            const currentValues = prev[name];
            if (checked) {
                return { ...prev, [name]: [...currentValues, value] };
            } else {
                return { ...prev, [name]: currentValues.filter(item => item !== value) };
            }
        });
    };

    // Sahibinden.com için hiyerarşik ve yapısal URL'i oluşturan fonksiyon
    const redirectToSahibinden = () => {
        let baseUrl = 'https://www.sahibinden.com/otomobil';

        // Boşlukları '-' ile değiştirip küçük harfe çeviren yardımcı fonksiyon
        const formatForUrl = (text) => text.trim().toLowerCase().replace(/\s+/g, '-');

        // ANAHTAR DEĞİŞİKLİK: URL'i hiyerarşik olarak oluşturuyoruz
        // Vasıta > Otomobil > Marka-Model > Seri > Paket
        if (filters.brand) {
            let pathSegment = formatForUrl(filters.brand);
            if (filters.model) {
                pathSegment += `-${formatForUrl(filters.model)}`;
            }
            baseUrl += `/${pathSegment}`;

            // Sadece marka ve model girildiyse değil, seri ve paket de varsa ekle
            if (filters.series) {
                baseUrl += `/${formatForUrl(filters.series)}`;
                if (filters.trimPackage) {
                    baseUrl += `/${formatForUrl(filters.trimPackage)}`;
                }
            }
        }
        
        // Diğer filtreleri URL parametreleri olarak hazırlıyoruz
        const params = new URLSearchParams();
        if (filters.yearMin) params.append('a5_min', filters.yearMin);
        if (filters.yearMax) params.append('a5_max', filters.yearMax);
        if (filters.kmMax) params.append('a4_max', filters.kmMax);
        
        filters.fuelTypes.forEach(fuel => params.append('a3', fuel));
        filters.gearTypes.forEach(gear => params.append('a2', gear));

        const paramString = params.toString();
        const finalUrl = paramString ? `${baseUrl}?${paramString}` : baseUrl;

        console.log("Oluşturulan Hiyerarşik URL:", finalUrl);

        window.open(finalUrl, '_blank');
    };

    return (
        <div className="market-page-wrapper">
            <div className="market-page-container">
                <div className="card">
                    <h3>Piyasa Analizi için Araç Filtrele</h3>
                    <p className="description">
                        En doğru sonuçlar için aşağıdaki tüm alanları doldurmaya çalışın.
                    </p>

                    <div className="filter-grid">
                        <div className="form-group">
                            <label>Marka</label>
                            <input name="brand" value={filters.brand} onChange={handleInputChange} placeholder="örn: Opel" />
                        </div>
                        <div className="form-group">
                            <label>Model</label>
                            <input name="model" value={filters.model} onChange={handleInputChange} placeholder="örn: Astra" />
                        </div>
                        {/* YENİ ALANLAR */}
                        <div className="form-group">
                            <label>Seri (Motor)</label>
                            <input name="series" value={filters.series} onChange={handleInputChange} placeholder="örn: 1.3 CDTI" />
                        </div>
                        <div className="form-group">
                            <label>Donanım Paketi</label>
                            <input name="trimPackage" value={filters.trimPackage} onChange={handleInputChange} placeholder="örn: Enjoy Plus" />
                        </div>
                        {/*  */}
                        <div className="form-group">
                            <label>Yıl (En Az)</label>
                            <input name="yearMin" type="number" value={filters.yearMin} onChange={handleInputChange} placeholder="örn: 2010" />
                        </div>
                        <div className="form-group">
                            <label>Yıl (En Çok)</label>
                            <input name="yearMax" type="number" value={filters.yearMax} onChange={handleInputChange} placeholder="örn: 2015" />
                        </div>
                        <div className="form-group">
                            <label>Kilometre (En Çok)</label>
                            <input name="kmMax" type="number" value={filters.kmMax} onChange={handleInputChange} placeholder="örn: 150000" />
                        </div>
                        
                        <div className="checkbox-group">
                           {/* Checkboxlar aynı kalabilir */}
                        </div>
                    </div>
                    
                    <div className="button-container">
                        <button className="redirect-button" onClick={redirectToSahibinden}>
                            Sahibinden.com'da Piyasayı Gör
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default MarketValuePage;
