import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './HomePage.css';

function HomePage() {
  const [vehicles, setVehicles] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedVehicle, setSelectedVehicle] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showMessageForm, setShowMessageForm] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  
  // Mesaj formu state'leri
  const [messageForm, setMessageForm] = useState({
    sender_name: '',
    sender_email: '',
    sender_phone: '',
    message: ''
  });
  const [messageSending, setMessageSending] = useState(false);
  
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAllVehicles = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/vehicles');
        setVehicles(response.data);
      } catch (error) {
        console.error("Tüm araçlar çekilirken hata oluştu:", error);
      } finally {
        setIsLoading(false);
      }
    };
    fetchAllVehicles();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  // Araç kartına tıklandığında detayları getir
  const handleVehicleClick = async (vehicleId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/vehicles/${vehicleId}`);
      setSelectedVehicle(response.data);
      setCurrentImageIndex(0);
      setShowModal(true);
    } catch (error) {
      console.error("Araç detayları alınamadı:", error);
      alert("Araç detayları yüklenirken bir hata oluştu.");
    }
  };

  const closeModal = () => {
    setShowModal(false);
    setShowMessageForm(false);
    setSelectedVehicle(null);
    setMessageForm({ sender_name: '', sender_email: '', sender_phone: '', message: '' });
  };

  // Resim navigasyonu
  const nextImage = () => {
    if (selectedVehicle && selectedVehicle.photos && selectedVehicle.photos.length > 0) {
      setCurrentImageIndex((prevIndex) => 
        prevIndex === selectedVehicle.photos.length - 1 ? 0 : prevIndex + 1
      );
    }
  };

  const prevImage = () => {
    if (selectedVehicle && selectedVehicle.photos && selectedVehicle.photos.length > 0) {
      setCurrentImageIndex((prevIndex) => 
        prevIndex === 0 ? selectedVehicle.photos.length - 1 : prevIndex - 1
      );
    }
  };

  // Mesaj gönderme
  const handleMessageSubmit = async (e) => {
    e.preventDefault();
    setMessageSending(true);
    
    try {
      await axios.post(`http://localhost:5000/api/vehicles/${selectedVehicle.id}/send-message`, messageForm);
      alert('Mesajınız başarıyla gönderildi! En kısa sürede size dönüş yapacağız.');
      setShowMessageForm(false);
      setMessageForm({ sender_name: '', sender_email: '', sender_phone: '', message: '' });
    } catch (error) {
      console.error("Mesaj gönderilemedi:", error);
      alert('Mesaj gönderilirken bir hata oluştu. Lütfen tekrar deneyin.');
    } finally {
      setMessageSending(false);
    }
  };

  const handleMessageInputChange = (e) => {
    setMessageForm(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  if (isLoading) return <div className="loading-container">İlanlar Yükleniyor...</div>;

  return (
    <div className="home-page-container">
      <header className="home-header">
        <h1 className="brand-title">Bayramlar Auto Ailesine Hoş Geldiniz.</h1>
        <button onClick={handleLogout} className="logout-button">Çıkış Yap</button>
      </header>
      
      <main className="vehicle-gallery">
        {vehicles.map(vehicle => (
          <div key={vehicle.id} className="vehicle-card-item" onClick={() => handleVehicleClick(vehicle.id)}>
            <div className="card-image-container">
              <img 
                src={`http://localhost:5000/${vehicle.photo_url}`} 
                alt={`${vehicle.brand} ${vehicle.model}`}
                onError={(e) => { e.target.onerror = null; e.target.src='https://via.placeholder.com/400x300?text=Resim+Yok'; }}
              />
            </div>
            <div className="card-content">
              <h3 className="vehicle-title">{vehicle.brand} {vehicle.model}</h3>
              <p className="vehicle-specs">{vehicle.year} • {vehicle.mileage.toLocaleString('tr-TR')} km • {vehicle.fuel}</p>
              <div className="vehicle-price">
                {parseFloat(vehicle.sale_price).toLocaleString('tr-TR', { style: 'currency', currency: 'TRY' })}
              </div>
            </div>
          </div>
        ))}
      </main>

      {/* ARAÇ DETAY MODAL */}
      {showModal && selectedVehicle && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="modal-close-btn" onClick={closeModal}>×</button>
            
            <div className="modal-body">
              {/* Sol taraf - Resimler */}
              <div className="modal-images-section">
                {selectedVehicle.photos && selectedVehicle.photos.length > 0 ? (
                  <div className="image-gallery">
                    <div className="main-image-container">
                      <img 
                        src={`http://localhost:5000/${selectedVehicle.photos[currentImageIndex].photo_url}`}
                        alt={`${selectedVehicle.brand} ${selectedVehicle.model}`}
                        className="main-image"
                      />
                      {selectedVehicle.photos.length > 1 && (
                        <>
                          <button className="image-nav-btn prev-btn" onClick={prevImage}>‹</button>
                          <button className="image-nav-btn next-btn" onClick={nextImage}>›</button>
                        </>
                      )}
                    </div>
                    
                    {selectedVehicle.photos.length > 1 && (
                      <div className="thumbnail-container">
                        {selectedVehicle.photos.map((photo, index) => (
                          <img
                            key={photo.id}
                            src={`http://localhost:5000/${photo.photo_url}`}
                            alt={`Thumbnail ${index + 1}`}
                            className={`thumbnail ${index === currentImageIndex ? 'active' : ''}`}
                            onClick={() => setCurrentImageIndex(index)}
                          />
                        ))}
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="no-image-placeholder">
                    <img src="https://via.placeholder.com/400x300?text=Resim+Yok" alt="Resim Yok" />
                  </div>
                )}
              </div>

              {/* Sağ taraf - Bilgiler */}
              <div className="modal-info-section">
                <h2 className="vehicle-modal-title">{selectedVehicle.brand} {selectedVehicle.model}</h2>
                
                <div className="vehicle-details-grid">
                  <div className="detail-item">
                    <span className="detail-label">Yıl:</span>
                    <span className="detail-value">{selectedVehicle.year}</span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">Kilometre:</span>
                    <span className="detail-value">{selectedVehicle.mileage.toLocaleString('tr-TR')} km</span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">Yakıt:</span>
                    <span className="detail-value">{selectedVehicle.fuel}</span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">Vites:</span>
                    <span className="detail-value">{selectedVehicle.gear}</span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">Renk:</span>
                    <span className="detail-value">{selectedVehicle.color}</span>
                  </div>
                </div>

                <div className="price-section">
                  <span className="price-label">Satış Fiyatı</span>
                  <span className="price-value">
                    {parseFloat(selectedVehicle.sale_price).toLocaleString('tr-TR', { style: 'currency', currency: 'TRY' })}
                  </span>
                </div>

                {selectedVehicle.description && (
                  <div className="description-section">
                    <h4>Açıklama</h4>
                    <p>{selectedVehicle.description}</p>
                  </div>
                )}

                <div className="action-buttons">
                  <button 
                    className="message-btn"
                    onClick={() => setShowMessageForm(!showMessageForm)}
                  >
                    {showMessageForm ? 'Mesaj Formunu Kapat' : 'Mesaj Gönder'}
                  </button>
                </div>

                {/* MESAJ FORMU */}
                {showMessageForm && (
                  <div className="message-form-container">
                    <h4>Bu araçla ilgili mesaj gönderin</h4>
                    <form onSubmit={handleMessageSubmit} className="message-form">
                      <div className="form-row">
                        <input
                          type="text"
                          name="sender_name"
                          placeholder="Adınız Soyadınız *"
                          value={messageForm.sender_name}
                          onChange={handleMessageInputChange}
                          required
                        />
                        <input
                          type="email"
                          name="sender_email"
                          placeholder="E-posta Adresiniz *"
                          value={messageForm.sender_email}
                          onChange={handleMessageInputChange}
                          required
                        />
                      </div>
                      <input
                        type="tel"
                        name="sender_phone"
                        placeholder="Telefon Numaranız (İsteğe bağlı)"
                        value={messageForm.sender_phone}
                        onChange={handleMessageInputChange}
                      />
                      <textarea
                        name="message"
                        placeholder="Mesajınızı buraya yazın... *"
                        value={messageForm.message}
                        onChange={handleMessageInputChange}
                        rows="4"
                        required
                      />
                      <button type="submit" disabled={messageSending} className="send-message-btn">
                        {messageSending ? 'Gönderiliyor...' : 'Mesajı Gönder'}
                      </button>
                    </form>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default HomePage;
