import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './VehiclesPage.css';

function VehiclesPage() {
  const [form, setForm] = useState({
    brand: '', model: '', year: '', color: '', gear: '', fuel: '', mileage: '',
    damage: '', purchase_price: '', sale_price: '', description: ''
  });
  const [vehicles, setVehicles] = useState([]);
  const [message, setMessage] = useState('');
  const [editId, setEditId] = useState(null);

  const fetchVehicles = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/vehicles');
      setVehicles(res.data);
    } catch (err) {
      console.error('Araçlar alınamadı', err);
    }
  };

  const clearForm = () => {
    setForm({
      brand: '', model: '', year: '', color: '', gear: '', fuel: '', mileage: '',
      damage: '', purchase_price: '', sale_price: '', description: ''
    });
    setEditId(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await axios.put(`http://localhost:5000/api/vehicles/${editId}`, form);
        setMessage('Araç güncellendi.');
      } else {
        await axios.post('http://localhost:5000/api/vehicles', form);
        setMessage('Araç başarıyla eklendi.');
      }
      clearForm();
      fetchVehicles();
    } catch (err) {
      console.error(err);
      setMessage('İşlem sırasında hata oluştu.');
    }
  };

  useEffect(() => {
    fetchVehicles();
  }, []);

  return (
    <div className="vehicles-container">
      <h2>{editId ? 'Araç Güncelle' : 'Yeni Araç Ekle'}</h2>
      <form className="vehicle-form" onSubmit={handleSubmit}>
        <input placeholder="Marka" value={form.brand} onChange={(e) => setForm({ ...form, brand: e.target.value })} required />
        <input placeholder="Model" value={form.model} onChange={(e) => setForm({ ...form, model: e.target.value })} required />
        <input placeholder="Yıl" type="number" value={form.year} onChange={(e) => setForm({ ...form, year: e.target.value })} required />
        <input placeholder="Renk" value={form.color} onChange={(e) => setForm({ ...form, color: e.target.value })} />
        <input placeholder="Vites Tipi" value={form.gear} onChange={(e) => setForm({ ...form, gear: e.target.value })} />
        <input placeholder="Yakıt Türü" value={form.fuel} onChange={(e) => setForm({ ...form, fuel: e.target.value })} />
        <input placeholder="Kilometre" type="number" value={form.mileage} onChange={(e) => setForm({ ...form, mileage: e.target.value })} />
        <input placeholder="Alınan Fiyat" type="number" value={form.purchase_price} onChange={(e) => setForm({ ...form, purchase_price: e.target.value })} />
        <input placeholder="Satılan Fiyat" type="number" value={form.sale_price} onChange={(e) => setForm({ ...form, sale_price: e.target.value })} />
        <input placeholder="Hasar Kaydı" value={form.damage} onChange={(e) => setForm({ ...form, damage: e.target.value })} />
        <textarea placeholder="Açıklama" value={form.description} onChange={(e) => setForm({ ...form, description: e.target.value })} />
        <button type="submit">{editId ? 'Güncelle' : 'Ekle'}</button>
      </form>

      {message && <p className="message">{message}</p>}

      <h3>Kayıtlı Araçlar</h3>
      <table className="vehicle-table">
        <thead>
          <tr>
            <th>Marka</th><th>Model</th><th>Yıl</th><th>Renk</th><th>Vites</th><th>Yakıt</th><th>KM</th>
            <th>Alış</th><th>Satış</th><th>Hasar</th><th>Açıklama</th><th>İşlem</th>
          </tr>
        </thead>
        <tbody>
          {vehicles.map(vehicle => (
            <tr key={vehicle.id}>
              <td>{vehicle.brand}</td><td>{vehicle.model}</td><td>{vehicle.year}</td><td>{vehicle.color}</td>
              <td>{vehicle.gear}</td><td>{vehicle.fuel}</td><td>{vehicle.mileage}</td>
              <td>{vehicle.purchase_price} ₺</td><td>{vehicle.sale_price} ₺</td>
              <td>{vehicle.damage}</td><td>{vehicle.description}</td>
              <td>
                <button onClick={() => {
                  setEditId(vehicle.id);
                  setForm({
                    brand: vehicle.brand, model: vehicle.model, year: vehicle.year,
                    color: vehicle.color, gear: vehicle.gear, fuel: vehicle.fuel,
                    mileage: vehicle.mileage, damage: vehicle.damage,
                    purchase_price: vehicle.purchase_price, sale_price: vehicle.sale_price,
                    description: vehicle.description
                  });
                }}>
                  Güncelle
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default VehiclesPage;
