import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';

// KORUYUCULAR
// './ProtectedRoute' yolunun doğru olduğundan emin olun. Eğer components klasöründeyse './components/ProtectedRoute' olmalı.
import ProtectedRoute from './ProtectedRoute'; 
// './components/AdminProtectedRoute' yolunun doğru olduğundan emin olun.
import AdminProtectedRoute from './components/AdminProtectedRoute'; 

// SAYFALAR
import LoginPage from './LoginPage';
import ForgotPasswordPage from './ForgotPasswordPage';

// KULLANICI SAYFALARI
import Dashboard from './Dashboard';
import VehiclesPage from './VehiclesPage';
import PersonnelPage from './PersonnelPage';
import CreditCalculatorPage from './CreditCalculatorPage';
import MarketValuePage from './MarketValuePage';

// YÖNETİCİ SAYFALARI (Test için basit bir AdminDashboard oluşturmanız gerekebilir)
import AdminDashboard from './AdminDashboard'; // Bu dosyayı oluşturduğunuzu varsayıyorum

function App() {
  return (
    <Router>
      <Routes>
        {/* ======================================= */}
        {/*      HERKESE AÇIK ROTALAR               */}
        {/* ======================================= */}
        <Route path="/" element={<LoginPage />} />
        <Route path="/forgot-password" element={<ForgotPasswordPage />} />


        {/* ======================================= */}
        {/*      NORMAL KULLANICI ROTALARI          */}
        {/* ======================================= */}
        {/* Bu rotalar ProtectedRoute ile korunuyor. Giriş yapmış HERKES (admin veya user) görebilir. */}
        <Route path="/dashboard" element={<ProtectedRoute><Dashboard /></ProtectedRoute>} />
        <Route path="/vehicles" element={<ProtectedRoute><VehiclesPage /></ProtectedRoute>} />
        <Route path="/personnel" element={<ProtectedRoute><PersonnelPage /></ProtectedRoute>} />
        <Route path="/kredi" element={<ProtectedRoute><CreditCalculatorPage /></ProtectedRoute>} />
        <Route path="/piyasa" element={<ProtectedRoute><MarketValuePage /></ProtectedRoute>} />


        {/* ======================================= */}
        {/*      YÖNETİCİYE ÖZEL ROTALAR            */}
        {/* ======================================= */}
        {/* Bu rotalar AdminProtectedRoute ile korunuyor. SADECE rolü 'admin' olanlar görebilir. */}
        <Route 
          path="/admin-dashboard" 
          element={
            <AdminProtectedRoute>
              <AdminDashboard />
            </AdminProtectedRoute>
          } 
        />
        {/* Gelecekte ekleyeceğiniz diğer admin sayfalarını da buraya ekleyebilirsiniz. */}
        {/* Örnek:
        <Route 
          path="/admin/settings" 
          element={<AdminProtectedRoute><AdminSettingsPage /></AdminProtectedRoute>} 
        />
        */}

      </Routes>
    </Router>
  );
}

export default App;
