import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import AdminChatBox from './AdminChatBox';
import './MessagesPage.css';
import { useNavigate } from 'react-router-dom'; // *** YENİ: Yönlendirme için eklendi
import { socket } from './socket'; // *** YENİ: Anlık güncellemeler için socket eklendi

function MessagesPage() {
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate(); // *** YENİ: navigate fonksiyonu kullanıma hazır

  const fetchConversations = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Yetkilendirme token\'ı bulunamadı. Lütfen tekrar giriş yapın.');
        setIsLoading(false);
        navigate('/'); // Token yoksa login'e yönlendir
        return;
      }

      const response = await axios.get('http://localhost:5000/api/conversations', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      
      setConversations(response.data);
      setError('');
    } catch (error) {
      console.error("Konuşmalar yüklenemedi:", error);
      const errorMessage = error.response?.data?.message || 'Konuşmalar yüklenirken bir hata oluştu.';
      setError(errorMessage);
      
      if (error.response?.status === 403 || error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/');
      }
    } finally {
      setIsLoading(false);
    }
  }, [navigate]); // navigate bağımlılıklara eklendi

  // useEffect(() => {
  //   socket.connect(); // Socket bağlantısını başlat
  //   fetchConversations();

  //   // *** YENİ: Socket üzerinden listeyi yenileme
  //   const handleRefresh = () => fetchConversations();
  //   const handleConversationDeleted = (deletedConvId) => {
  //       // Eğer silinen sohbet ekranda seçili ise, seçimi kaldır
  //       if (selectedConversation?.conversation_id === deletedConvId) {
  //           setSelectedConversation(null);
  //       }
  //       fetchConversations(); // Listeyi anında yenile
  //   };

  //   socket.on('admin_refresh_conversations', handleRefresh);
  //   socket.on('conversation_deleted', handleConversationDeleted);

  //   // Component kaldırıldığında (sayfadan ayrıldığında) socket bağlantısını kes ve dinleyicileri kaldır
  //   return () => {
  //     socket.off('admin_refresh_conversations', handleRefresh);
  //     socket.off('conversation_deleted', handleConversationDeleted);
  //     socket.disconnect();
  //   };
  // }, [fetchConversations, selectedConversation]); // selectedConversation buraya eklendi
  // BU KOD BLOĞUNU MEVCUT useEffect BLOĞUNUZLA DEĞİŞTİRİN

  // useEffect(() => {
  //   // Socket bağlantısını başlat (eğer zaten bağlı değilse)
  //   if (!socket.connected) {
  //     socket.connect();
  //   }

  //   socket.emit('admin_cleared_notifications');

  //   fetchConversations();

  //   // Socket üzerinden gelen sinyallerle listeyi güncel tutacak fonksiyonlar
  //   const handleRefresh = () => fetchConversations();
  //   const handleConversationDeleted = (deletedConvId) => {
  //       // Eğer silinen sohbet ekranda seçili ise, seçimi kaldır
  //       if (selectedConversation?.conversation_id === deletedConvId) {
  //           setSelectedConversation(null);
  //       }
  //       fetchConversations(); // Listeyi anında yenile
  //   };

  //   // Dinleyicileri ata
  //   socket.on('admin_refresh_conversations', handleRefresh);
  //   socket.on('conversation_deleted', handleConversationDeleted);

  //   // Temizlik fonksiyonu: Component DOM'dan kaldırıldığında (sayfadan ayrıldığında) çalışır
  //   return () => {
  //     socket.off('admin_refresh_conversations', handleRefresh);
  //     socket.off('conversation_deleted', handleConversationDeleted);
  //     // Not: Bağlantıyı burada kesmek yerine App.js'de yönetmek daha stabil olabilir,
  //     // ama mevcut yapınıza uygun olarak burada bırakıyorum.
  //     socket.disconnect();
  //   };
  // }, [fetchConversations, selectedConversation]); // selectedConversation bağımlılığı korundu
useEffect(() => {
    // Socket bağlantısının aktif olduğundan emin ol
    if (!socket.connected) {
      socket.connect();
    }

    // *** EN ÖNEMLİ DEĞİŞİKLİK ***
    // Bu sayfa yüklendiği anda, App.js'deki merkezi sayacı sıfırlaması için
    // sunucuya "bildirimleri temizledim" sinyalini gönder.
    socket.emit('admin_cleared_notifications');

    // Mevcut konuşmaları her zamanki gibi çek
    fetchConversations();

    // Socket üzerinden gelen sinyallerle listeyi güncel tutacak fonksiyonlar
    const handleRefresh = () => fetchConversations();
    const handleConversationDeleted = (deletedConvId) => {
        if (selectedConversation?.conversation_id === deletedConvId) {
            setSelectedConversation(null);
        }
        fetchConversations();
    };

    // Dinleyicileri ata
    socket.on('admin_refresh_conversations', handleRefresh);
    socket.on('conversation_deleted', handleConversationDeleted);

    // Temizlik fonksiyonu
    return () => {
      // Bu sayfadan ayrılırken dinleyicileri kaldırarak bellek sızıntılarını önle
      socket.off('admin_refresh_conversations', handleRefresh);
      socket.off('conversation_deleted', handleConversationDeleted);
      // Not: Ana bağlantı App.js tarafından yönetileceği için,
      // buradan ayrılırken bağlantıyı tamamen kesmiyoruz.
      // Bu, diğer sayfalara geçtiğinizde bağlantının kopmamasını sağlar.
    };
  }, [fetchConversations, selectedConversation]);
  // *** YENİ: Sohbeti kalıcı olarak silme fonksiyonu
  const handleDeleteConversation = async (e, conversationId) => {
    e.stopPropagation(); // Butonun arkasındaki div'in tıklanmasını engelle
    if (window.confirm("Bu sohbeti ve içindeki tüm mesajları kalıcı olarak silmek istediğinizden emin misiniz?")) {
        try {
            const token = localStorage.getItem('token');
            await axios.delete(`http://localhost:5000/api/conversations/${conversationId}`, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            // Arayüzün güncellenmesi socket.io tarafından otomatik olarak tetiklenecek
        } catch (error) {
            alert(error.response?.data?.message || "Sohbet silinirken bir hata oluştu.");
        }
    }
  };


  const refreshConversations = () => {
    setIsLoading(true);
    fetchConversations();
  };

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Gelen Kutusu Yükleniyor...</div>
      </div>
    );
  }

  if (error && conversations.length === 0) {
    return (
      <div className="error-container">
        <p>Hata: {error}</p>
        <button onClick={refreshConversations}>Tekrar Dene</button>
      </div>
    );
  }

  return (
    <div className="messages-page-container">
      <div className="conversations-sidebar">
        {/* *** YENİ: "Panele Dön" butonu ve yeni header düzeni *** */}
        <div className="sidebar-header">
          <button onClick={() => navigate('/dashboard')} className="back-to-dashboard-btn" title="Yönetim Paneline Dön">
            ‹ Panele Dön
          </button>
          <h2>Gelen Kutusu</h2>
          <button onClick={refreshConversations} className="refresh-btn" title="Yenile">
            🔄
          </button>
        </div>
        
        <div className="conversations-list">
          {conversations.length === 0 ? (
            <div className="no-conversations">
              <p>Henüz bir görüşme başlatılmamış.</p>
            </div>
          ) : (
            conversations.map((convo) => (
              <div 
                key={convo.conversation_id} 
                className={`conversation-item ${
                  selectedConversation?.conversation_id === convo.conversation_id ? 'active' : ''
                }`}
                onClick={() => setSelectedConversation(convo)}
              >
                <div className="conversation-avatar">
                  {convo.user_name?.charAt(0).toUpperCase() || '?'}
                </div>
                
                <div className="conversation-content">
                  <div className="conversation-header">
                    <h4>{convo.user_name || 'Bilinmeyen Kullanıcı'}</h4>
                    <span className="conversation-time">
                      {new Date(convo.created_at).toLocaleString('tr-TR', {
                        hour: '2-digit', minute: '2-digit'
                      })}
                    </span>
                  </div>
                  
                  <div className="vehicle-info">
                    <span className="vehicle-name">{convo.brand} {convo.model}</span>
                  </div>
                  
                  <p className="last-message">{convo.message}</p>
                </div>
                {/* *** YENİ: Her sohbet için silme butonu *** */}
                <button 
                    className="delete-conversation-btn" 
                    title="Sohbeti Sil"
                    onClick={(e) => handleDeleteConversation(e, convo.conversation_id)}
                >
                    🗑️
                </button>
              </div>
            ))
          )}
        </div>
      </div>

      <div className="chat-area">
        {selectedConversation ? (
          <AdminChatBox 
            key={selectedConversation.conversation_id}
            conversationId={selectedConversation.conversation_id}
          />
        ) : (
          <div className="no-chat-selected">
            <div className="no-chat-content">
              <h3>Hoş Geldiniz!</h3>
              <p>Mesajları görüntülemek için sol menüden bir konuşma seçin.</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default MessagesPage;
