import React, { useEffect } from 'react'; // *** YENİ: useEffect eklendi
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import { jwtDecode } from 'jwt-decode'; // *** YENİ: Token'ı okumak için eklendi
import { socket } from './socket';     // *** YENİ: Merkezi socket import edildi

// KORUYUCULAR (Mevcut importlarınız korundu)
import ProtectedRoute from './ProtectedRoute'; 
import AdminProtectedRoute from './AdminProtectedRoute'; 

// SAYFALAR (Mevcut importlarınız korundu)
import LoginPage from './LoginPage';
import ForgotPasswordPage from './ForgotPasswordPage';
import HomePage from './HomePage';
import Dashboard from './Dashboard';
import VehiclesPage from './VehiclesPage';
import PersonnelPage from './PersonnelPage';
import CreditCalculatorPage from './CreditCalculatorPage';
import MarketValuePage from './MarketValuePage';
import MessagesPage from './MessagesPage';

function App() {

  // *** YENİ: SESLİ BİLDİRİM VE GENEL SOCKET YÖNETİMİ ***
  useEffect(() => {
    const token = localStorage.getItem('token');
    let userRole = null;

    // Token varsa içini açıp kullanıcının rolünü al
    if (token) {
      try {
        userRole = jwtDecode(token).role;
      } catch (error) {
        console.error("App.js içinde geçersiz token:", error);
      }
    }

    // Bu mantık SADECE GİRİŞ YAPAN KİŞİ ADMİN İSE çalışır
    if (userRole === 'admin') {
      socket.connect(); // Socket bağlantısını başlat

      // Yeni mesaj geldiğinde sesi çalacak fonksiyon
      const playNotificationSound = () => {
        // Ses dosyasının public klasöründe olduğundan emin olun
        const audio = new Audio('/notification.mp3'); 
        audio.play().catch(error => console.log("Tarayıcı, kullanıcı etkileşimi olmadan sesi engelledi:", error));
      };

      // Backend'den 'admin_new_message_notification' sinyali geldiğinde sesi çal
      socket.on('admin_new_message_notification', playNotificationSound);

      // Temizlik fonksiyonu: Component DOM'dan kaldırıldığında (veya kullanıcı değiştiğinde) çalışır
      return () => {
        socket.off('admin_new_message_notification', playNotificationSound);
        socket.disconnect();
      };
    }
  }, []); // Köşeli parantezlerin boş olması, bu kodun sadece bir kere (uygulama ilk yüklendiğinde) çalışmasını sağlar.

  return (
    <Router>
      <Routes>
        {/* ======================================= */}
        {/*      HERKESE AÇIK ROTALAR               */}
        {/* ======================================= */}
        <Route path="/" element={<LoginPage />} />
        <Route path="/forgot-password" element={<ForgotPasswordPage />} />

        {/* ======================================= */}
        {/*      NORMAL KULLANICIYA ÖZEL ROTA       */}
        {/* ======================================= */}
        <Route 
          path="/home" 
          element={<ProtectedRoute><HomePage /></ProtectedRoute>} 
        />

        {/* ======================================= */}
        {/*      YÖNETİCİYE ÖZEL ROTALAR            */}
        {/* ======================================= */}
        <Route 
          path="/dashboard" 
          element={<AdminProtectedRoute><Dashboard /></AdminProtectedRoute>} 
        />
        <Route 
          path="/admin/vehicles" 
          element={<AdminProtectedRoute><VehiclesPage /></AdminProtectedRoute>} 
        />
        <Route 
          path="/admin/personnel" 
          element={<AdminProtectedRoute><PersonnelPage /></AdminProtectedRoute>} 
        />
        <Route 
          path="/admin/kredi"
          element={<AdminProtectedRoute><CreditCalculatorPage /></AdminProtectedRoute>} 
        />
        <Route 
          path="/admin/piyasa" 
          element={<AdminProtectedRoute><MarketValuePage /></AdminProtectedRoute>} 
        />
        <Route 
          path="/admin/messages" 
          element={<AdminProtectedRoute><MessagesPage /></AdminProtectedRoute>} 
        />
      </Routes>
    </Router>
  );
}

export default App;
