import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';

// ===> DEĞİŞİKLİK 1: TUTARLI DOSYA YOLLARI <===
// Tüm koruyucuların 'src/components' klasöründe olduğunu varsayıyoruz.
import ProtectedRoute from './ProtectedRoute'; 
import AdminProtectedRoute from './AdminProtectedRoute';

// SAYFALAR
import LoginPage from './LoginPage';
import ForgotPasswordPage from './ForgotPasswordPage';

// KULLANICI SAYFALARI
import Dashboard from './Dashboard';
import VehiclesPage from './VehiclesPage';
import PersonnelPage from './PersonnelPage';
import CreditCalculatorPage from './CreditCalculatorPage';
import MarketValuePage from './MarketValuePage';

// YÖNETİCİ SAYFALARI
import AdminDashboard from './AdminDashboard';

function App() {
  return (
    <Router>
      <Routes>
        {/* Herkese Açık Rotalar */}
        <Route path="/" element={<LoginPage />} />
        <Route path="/forgot-password" element={<ForgotPasswordPage />} />

        {/* Normal Kullanıcı Rotaları */}
        <Route path="/dashboard" element={<ProtectedRoute><Dashboard /></ProtectedRoute>} />
        <Route path="/vehicles" element={<ProtectedRoute><VehiclesPage /></ProtectedRoute>} />
        <Route path="/personnel" element={<ProtectedRoute><PersonnelPage /></ProtectedRoute>} />
        <Route path="/kredi" element={<ProtectedRoute><CreditCalculatorPage /></ProtectedRoute>} />
        <Route path="/piyasa" element={<ProtectedRoute><MarketValuePage /></ProtectedRoute>} />
        
        {/* Yöneticiye Özel Rotalar */}
        <Route 
          path="/admin-dashboard" 
          element={
            <AdminProtectedRoute>
              <AdminDashboard />
            </AdminProtectedRoute>
          } 
        />
      </Routes>
    </Router>
  );
}

// ===> DEĞİŞİKLİK 2: DOĞRU BİLEŞENİ EXPORT ETMEK <===
// En sondaki bu satır en önemlisi!
export default App;
