import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Dashboard.css';
import { socket } from './socket';

function Dashboard() {
  const navigate = useNavigate();
  const [notificationCount, setNotificationCount] = useState(0);
  const [isSocketConnected, setIsSocketConnected] = useState(socket.connected);

  const handleLogout = () => {
    localStorage.removeItem('token');
    socket.disconnect();
    navigate('/');
  };

  const fetchConversationCount = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/conversations', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      setNotificationCount(response.data.length);
    } catch (error) {
      console.error("Sohbet sayısı alınamadı:", error);
    }
  }, []);

  useEffect(() => {
    // Sayfa ilk yüklendiğinde mevcut sohbet sayısını al
    fetchConversationCount();

    // Socket bağlantısı yoksa kur
    if (!isSocketConnected) {
      socket.connect();
    }

    // Bağlantı kurulduğunda state'i güncelle
    const onConnect = () => setIsSocketConnected(true);
    socket.on('connect', onConnect);

    const handleNewMessage = () => {
        setNotificationCount(prevCount => prevCount + 1);
    };
    
    // Sunucudan sıfırlama sinyali geldiğinde sayacı sıfırla
    const handleResetNotifications = () => {
        setNotificationCount(0);
    };

    socket.on('admin_new_message_notification', handleNewMessage);
    socket.on('reset_notifications', handleResetNotifications);
    
    const handleRefresh = () => fetchConversationCount();
    socket.on('admin_refresh_conversations', handleRefresh);

    return () => {
      socket.off('connect', onConnect);
      socket.off('admin_new_message_notification', handleNewMessage);
      socket.off('reset_notifications', handleResetNotifications);
      socket.off('admin_refresh_conversations', handleRefresh);
      socket.disconnect();
    };
  }, [fetchConversationCount, isSocketConnected]);

  // *** GÜNCELLEME BURADA: Bu fonksiyonun içeriği değiştirildi ***
  // Artık sunucuya sinyal göndermek yerine, sayacı doğrudan kendisi sıfırlıyor
  // ve SONRA sayfayı değiştiriyor.
  const handleNavigateToMessages = () => {
    setNotificationCount(0); // Sayacı anında, yerel olarak sıfırla
    navigate('/admin/messages'); // Sonra Mesajlar sayfasına git
  };

  return (
    <div className="dashboard-wrapper">
        <div className="car-animation-lane">
            <div className="car"></div>
            <div className="car"></div>
            <div className="car"></div>
            <div className="car"></div>
        </div>
        <div className="dashboard-container">
            <header className="dashboard-header">
                <span>Yönetici Paneli</span>
                <button onClick={handleLogout} className="logout-button">
                    Çıkış Yap
                </button>
            </header>
            <main className="dashboard-content">
                <div className="title-section">
                    <h1>🚗 BayramlarAuto Yönetim Paneli</h1>
                    <p>Hoş geldiniz! Ne yapmak istersiniz?</p>
                </div>
                <nav className="navigation-buttons">
                    <button onClick={() => navigate('/admin/vehicles')}>Araç Yönetimi</button>
                    <button onClick={() => navigate('/admin/personnel')}>Personel Yönetimi</button>
                    <button onClick={() => navigate('/admin/kredi')}>Kredi Hesaplama</button>
                    <button onClick={() => navigate('/admin/piyasa')}>Piyasa Değeri</button>
                    
                    
                    <button className="messages-button" onClick={handleNavigateToMessages}>
                        Gelen Mesajlar
                        {notificationCount > 0 && (
                            <span className="notification-badge">{notificationCount}</span>
                        )}
                    </button>
                </nav>
            </main>
        </div>
    </div>
  );
}

export default Dashboard;
