import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AdminChatBox from './AdminChatBox';
import './MessagesPage.css';

function MessagesPage() {
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchConversations = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/conversations', {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        setConversations(response.data);
      } catch (error) {
        console.error("Konuşmalar yüklenemedi:", error);
      } finally {
        setIsLoading(false);
      }
    };
    fetchConversations();
  }, []);

  if (isLoading) return <div className="loading-container">Gelen Kutusu Yükleniyor...</div>;

  return (
    <div className="messages-page-container">
      <div className="conversations-list">
        <div className="list-header">
          <h2>Gelen Kutusu</h2>
        </div>
        <div className="list-content">
          {conversations.length === 0 ? (
            <p className="no-messages">Hiç mesajınız yok.</p>
          ) : (
            conversations.map(convo => (
              <div 
                key={convo.conversation_id} 
                className={`conversation-item ${selectedConversation?.conversation_id === convo.conversation_id ? 'active' : ''}`}
                onClick={() => setSelectedConversation(convo)}
              >
                <div className="convo-info">
                  {/* ===> DEĞİŞİKLİK BURADA: Artık kullanıcının adı görünüyor <=== */}
                  <strong>{convo.user_name}</strong>
                  <span>{convo.brand} {convo.model}</span>
                </div>
                <p className="last-message">{convo.message}</p>
                <span className="convo-time">{new Date(convo.created_at).toLocaleString('tr-TR')}</span>
              </div>
            ))
          )}
        </div>
      </div>
      <div className="chat-box-area">
        {selectedConversation ? (
          // ===> DEĞİŞİKLİK BURADA: Sohbet kutusuna user_id ve vehicle_id de gönderiliyor <===
          <AdminChatBox 
            key={selectedConversation.conversation_id} // Konuşma değiştiğinde bileşenin yeniden render olmasını sağlar
            conversationId={selectedConversation.conversation_id} 
            vehicleId={selectedConversation.vehicle_id}
            userId={selectedConversation.user_id}
          />
        ) : (
          <div className="no-chat-selected">
            <p>Görüntülemek için bir konuşma seçin.</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default MessagesPage;
