import React, { useState } from 'react';
import './MarketValuePage.css'; // Özel CSS dosyanızın yolunu kontrol edin.

function MarketValuePage() {
    const [filters, setFilters] = useState({
        brand: '',
        model: '',
        yearMin: '',
        yearMax: '',
        kmMax: '',
        fuelTypes: [],
        gearTypes: [],
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFilters(prev => ({ ...prev, [name]: value }));
    };

    const handleCheckboxChange = (e) => {
        const { name, value, checked } = e.target;
        setFilters(prev => {
            const currentValues = prev[name];
            if (checked) {
                return { ...prev, [name]: [...currentValues, value] };
            } else {
                return { ...prev, [name]: currentValues.filter(item => item !== value) };
            }
        });
    };

    // Sahibinden.com için yapısal ve doğru URL'i oluşturan fonksiyon
    const redirectToSahibinden = () => {
        // Temel URL'i 'let' ile tanımlıyoruz çünkü üzerine ekleme yapacağız.
        let baseUrl = 'https://www.sahibinden.com/otomobil';

        // Boşlukları '-' ile değiştirip küçük harfe çeviren bir yardımcı fonksiyon
        const formatForUrl = (text) => text.trim().toLowerCase().replace(/\s+/g, '-');

        // ANAHTAR DEĞİŞİKLİK: Marka ve Modeli doğrudan URL yoluna ekliyoruz.
        if (filters.brand) {
            baseUrl += `/${formatForUrl(filters.brand)}`;
        }
        // Model, markadan sonra '-' ile eklenir.
        if (filters.model) {
            // Eğer marka girilmemişse, modelin başına '-' koymamak için kontrol
            if (filters.brand) {
                baseUrl += `-${formatForUrl(filters.model)}`;
            } else {
                // Sadece model girildiyse, bu Sahibinden'de genellikle çalışmaz ama
                // yine de bir arama metni olarak ekleyebiliriz. En iyisi marka ile kullanmaktır.
                baseUrl += `?query_text_mf=${formatForUrl(filters.model)}`;
            }
        }
        
        // Diğer filtreleri URL parametreleri olarak hazırlıyoruz
        const params = new URLSearchParams();
        if (filters.yearMin) params.append('a5_min', filters.yearMin);
        if (filters.yearMax) params.append('a5_max', filters.yearMax);
        if (filters.kmMax) params.append('a4_max', filters.kmMax);
        
        filters.fuelTypes.forEach(fuel => params.append('a3', fuel));
        filters.gearTypes.forEach(gear => params.append('a2', gear));

        // Parametreleri URL'e ekleyerek nihai adresi oluşturuyoruz.
        const paramString = params.toString();
        const finalUrl = paramString ? `${baseUrl}?${paramString}` : baseUrl;

        console.log("Oluşturulan Yapısal URL:", finalUrl);

        window.open(finalUrl, '_blank');
    };

    return (
        <div className="market-page-wrapper">
            <div className="market-page-container">
                <div className="card">
                    <h3>Piyasa Analizi için Araç Filtrele</h3>
                    <p className="description">
                        Aşağıdaki formu doldurarak, seçtiğiniz aracın piyasa durumunu popüler satış platformlarında anında görün.
                    </p>

                    <div className="filter-grid">
                        <div className="form-group">
                            <label>Marka</label>
                            <input name="brand" value={filters.brand} onChange={handleInputChange} placeholder="örn: Opel" />
                        </div>
                        <div className="form-group">
                            <label>Model</label>
                            <input name="model" value={filters.model} onChange={handleInputChange} placeholder="örn: Astra" />
                        </div>
                        <div className="form-group">
                            <label>Yıl (En Az)</label>
                            <input name="yearMin" type="number" value={filters.yearMin} onChange={handleInputChange} placeholder="örn: 2020" />
                        </div>
                        <div className="form-group">
                            <label>Yıl (En Çok)</label>
                            <input name="yearMax" type="number" value={filters.yearMax} onChange={handleInputChange} placeholder="örn: 2023" />
                        </div>
                        <div className="form-group">
                            <label>Kilometre (En Çok)</label>
                            <input name="kmMax" type="number" value={filters.kmMax} onChange={handleInputChange} placeholder="örn: 50000" />
                        </div>

                        <div className="checkbox-group">
                            <label>Yakıt Tipi</label>
                            <div>
                                <label><input type="checkbox" name="fuelTypes" value="1" onChange={handleCheckboxChange} /> Benzin</label>
                                <label><input type="checkbox" name="fuelTypes" value="2" onChange={handleCheckboxChange} /> Dizel</label>
                                <label><input type="checkbox" name="fuelTypes" value="3" onChange={handleCheckboxChange} /> LPG</label>
                            </div>
                        </div>
                        <div className="checkbox-group">
                            <label>Vites Tipi</label>
                            <div>
                                <label><input type="checkbox" name="gearTypes" value="1" onChange={handleCheckboxChange} /> Manuel</label>
                                <label><input type="checkbox" name="gearTypes" value="2" onChange={handleCheckboxChange} /> Otomatik</label>
                            </div>
                        </div>
                    </div>
                    
                    <div className="button-container">
                        <button className="redirect-button" onClick={redirectToSahibinden}>
                            Sahibinden.com'da Piyasayı Gör
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default MarketValuePage;
