import React, { useEffect, useState } from 'react';
import axios from 'axios';

function VehiclesPage() {
  const [form, setForm] = useState({
    brand: '',
    model: '',
    year: '',
    color: '',
    gear: '',
    fuel: '',
    mileage: '',
    purchase_price: '',
    sale_price: '',
    description: ''
    
  });

  const [vehicles, setVehicles] = useState([]);
  const [message, setMessage] = useState('');
  const [editId, setEditId] = useState(null);
  const [search, setSearch] = useState('');


  const fetchVehicles = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/vehicles');
      setVehicles(res.data);
    } catch (err) {
      console.error('Araçlar alınamadı', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      ...form,
      year: parseInt(form.year),
      mileage: parseInt(form.mileage),
      purchase_price: parseFloat(form.purchase_price),
      sale_price: parseFloat(form.sale_price)
    };

    try {
      if (editId) {
        // GÜNCELLEME
        await axios.put(`http://localhost:5000/api/vehicles/${editId}`, payload);
        setMessage('Araç başarıyla güncellendi.');
        setEditId(null);
      } else {
        // YENİ KAYIT
        await axios.post('http://localhost:5000/api/vehicles', payload);
        setMessage('Araç başarıyla eklendi.');
      }

      setForm({
        brand: '',
        model: '',
        year: '',
        color: '',
        gear: '',
        fuel: '',
        mileage: '',
        purchase_price: '',
        sale_price: '',
        description: ''
      });
      fetchVehicles();
    } catch (err) {
      console.error(err);
      setMessage('Araç kaydı sırasında hata oluştu.');
    }
  };

  const handleDelete = async (id) => {
  if (!window.confirm('Bu aracı silmek istediğinize emin misiniz?')) return;
  try {
    await axios.delete(`http://localhost:5000/api/vehicles/${id}`);
    // Eğer silinen araç düzenleniyorsa, formu ve editId'yi temizle
    if (editId === id) {
      setEditId(null);
      setForm({
        brand: '',
        model: '',
        year: '',
        color: '',
        gear: '',
        fuel: '',
        mileage: '',
        purchase_price: '',
        sale_price: '',
        description: ''
      });
    }
    setMessage('Araç silindi.');
    fetchVehicles(); // tabloyu otomatik güncelle
  } catch (err) {
    console.error('Silme hatası:', err);
    setMessage('Araç silinemedi.');
  }
};

  const handleEdit = (vehicle) => {
    setForm({
      brand: vehicle.brand,
      model: vehicle.model,
      year: vehicle.year,
      color: vehicle.color,
      gear: vehicle.gear,
      fuel: vehicle.fuel,
      mileage: vehicle.mileage,
      purchase_price: vehicle.purchase_price,
      sale_price: vehicle.sale_price,
      description: vehicle.description
    });
    setEditId(vehicle.id);
  };

  useEffect(() => {
    fetchVehicles();
  }, []);

  return (
    <div style={{ padding: '2rem', maxWidth: '1000px', margin: 'auto' }}>
      <h2>Araç Kayıt</h2>
      <form onSubmit={handleSubmit} style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '16px', marginBottom: '2rem' }}>
        <input placeholder="Marka" style={{ width: '100%', padding: '8px' }} value={form.brand} onChange={(e) => setForm({ ...form, brand: e.target.value })} required />
        <input placeholder="Model" style={{ width: '100%', padding: '8px' }} value={form.model} onChange={(e) => setForm({ ...form, model: e.target.value })} required />
        <input placeholder="Yıl" type="number" style={{ width: '100%', padding: '8px' }} value={form.year} onChange={(e) => setForm({ ...form, year: e.target.value })} required />
        <input placeholder="Renk" style={{ width: '100%', padding: '8px' }} value={form.color} onChange={(e) => setForm({ ...form, color: e.target.value })} />
        <input placeholder="Vites" style={{ width: '100%', padding: '8px' }} value={form.gear} onChange={(e) => setForm({ ...form, gear: e.target.value })} />
        <input placeholder="Yakıt" style={{ width: '100%', padding: '8px' }} value={form.fuel} onChange={(e) => setForm({ ...form, fuel: e.target.value })} />
        <input placeholder="Kilometre" type="number" style={{ width: '100%', padding: '8px' }} value={form.mileage} onChange={(e) => setForm({ ...form, mileage: e.target.value })} />
        <input placeholder="Alış Fiyatı" type="number" style={{ width: '100%', padding: '8px' }} value={form.purchase_price} onChange={(e) => setForm({ ...form, purchase_price: e.target.value })} />
        <input placeholder="Satış Fiyatı" type="number" style={{ width: '100%', padding: '8px' }} value={form.sale_price} onChange={(e) => setForm({ ...form, sale_price: e.target.value })} />
        <textarea
          placeholder="Açıklama"
          style={{ gridColumn: 'span 3', padding: '8px', height: '80px' }}
          value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })}
        ></textarea>
        <button type="submit" style={{ gridColumn: 'span 3', padding: '10px', fontWeight: 'bold' }}>
          {editId ? 'Güncelle' : 'Araç Ekle'}
        </button>
      </form>

      {message && <p>{message}</p>}

      <h3>Kayıtlı Araçlar</h3>
      <table border="1" cellPadding="10" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Marka</th>
            <th>Model</th>
            <th>Yıl</th>
            <th>Renk</th>
            <th>Vites</th>
            <th>Yakıt</th>
            <th>KM</th>
            <th>Alış</th>
            <th>Satış</th>
            <th>Açıklama</th>
            <th>İşlemler</th>
          </tr>
        </thead>
        <tbody>
          {vehicles.map(vehicle => (
            
            <tr key={vehicle.id}>
              <td>{vehicle.brand}</td>
              <td>{vehicle.model}</td>
              <td>{vehicle.year}</td>
              <td>{vehicle.color}</td>
              <td>{vehicle.gear}</td>
              <td>{vehicle.fuel}</td>
              <td>{vehicle.mileage}</td>
              <td>{vehicle.purchase_price} ₺</td>
              <td>{vehicle.sale_price} ₺</td>
              <td>{vehicle.description}</td>
              <td>
                <button onClick={() => handleEdit(vehicle)}>Düzenle</button>
                <button onClick={() => handleDelete(vehicle.id)} style={{ marginLeft: '8px', color: 'red' }}>Sil</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default VehiclesPage;
