// export default Dashboard;
import React from 'react';
import { useNavigate } from 'react-router-dom';

// Dashboard'a özel CSS dosyanızı import ediyoruz.
import './Dashboard.css'; 

function Dashboard() {
  const navigate = useNavigate();

  // ===> YENİ EKLENEN ÇIKIŞ YAPMA FONKSİYONU <===
  const handleLogout = () => {
    // Tarayıcı hafızasındaki token'ı sil.
    localStorage.removeItem('token');
    // Kullanıcıyı ana giriş sayfasına yönlendir.
    navigate('/');
  };

  // Not: Giriş kontrolü (if !token) bloğu buradan kaldırıldı.
  // Çünkü bu görevi artık App.js içindeki <ProtectedRoute> üstleniyor.
  // Bu, kod tekrarını önler ve daha temiz bir yapı sağlar.

  return (
    <div className="home-page-wrapper">
        
        {/* ===> YENİ EKLENEN ÜST BAR (HEADER) <=== */}
        {/* Çıkış yap butonunu diğer içerikten ayırmak için bir başlık alanı ekliyoruz. */}
        <header className="dashboard-header">
            <div className="header-content">
                <span>Hoş Geldiniz!</span>
                <button onClick={handleLogout} className="logout-button">
                    Çıkış Yap
                </button>
            </div>
        </header>
        
        <div className="animation-container">
            <div className="car car-1"></div>
            <div className="car car-2"></div>
            <div className="car car-3"></div>
            <div className="car car-4"></div>
        </div>

        <div className="content-container">
            <h1 className="main-title">
                🚗 BayramlarAuto Ana Sayfa
            </h1>
            <p className="subtitle">
                Ne yapmak istersiniz?
            </p>
            <nav className="navigation-buttons">
                <button onClick={() => navigate('/vehicles')}>Araç Kayıt</button>
                <button onClick={() => navigate('/personnel')}>Personel Kayıt</button>
                <button onClick={() => navigate('/kredi')}>Kredi Hesaplama</button>
                <button onClick={() => navigate('/piyasa')}>Piyasa Değeri</button>
            </nav>
        </div>

    </div>
  );
}

export default Dashboard;
