import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import './App.css';
import { jwtDecode } from 'jwt-decode';
import { socket } from './socket';
import axios from 'axios';

// KORUYUCULAR
import ProtectedRoute from './ProtectedRoute'; 
import AdminProtectedRoute from './AdminProtectedRoute'; 

// SAYFALAR
import LoginPage from './LoginPage';
import ForgotPasswordPage from './ForgotPasswordPage';
import HomePage from './HomePage';
import Dashboard from './Dashboard';
import VehiclesPage from './VehiclesPage';
import PersonnelPage from './PersonnelPage';
import CreditCalculatorPage from './CreditCalculatorPage';
import MarketValuePage from './MarketValuePage';
import MessagesPage from './MessagesPage';

function AppContent() {
  const [notificationCount, setNotificationCount] = useState(0);
  const location = useLocation();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;

    let userRole = null;
    try {
      userRole = jwtDecode(token).role;
    } catch (e) {
      // Token geçersizse işlemi durdur
      return;
    }

    if (userRole === 'admin') {
      if (!socket.connected) socket.connect();

      const fetchUnreadCount = () => {
        axios.get('http://localhost:5000/api/notifications/unread-count', { headers: { 'Authorization': `Bearer ${token}` }})
          .then(res => setNotificationCount(res.data.unreadCount))
          .catch(err => console.error("Okunmamış bildirim sayısı alınamadı:", err));
      };

      fetchUnreadCount();

      const playNotificationSound = () => {
        const audio = new Audio('/notification.mp3');
        audio.play().catch(e => console.log("Ses çalınamadı:", e));
      };

      const handleNewMessage = () => {
        playNotificationSound();
        setNotificationCount(prevCount => prevCount + 1);
      };

      const handleResetNotifications = () => setNotificationCount(0);

      // handleRefreshCount fonksiyonu, fetchUnreadCount ile aynı işi yaptığı için birleştirildi.
      // Bir sohbet silindiğinde, kalan okunmamış sohbet sayısını tekrar çeker.
      const handleRefresh = () => {
         fetchUnreadCount();
      };

      socket.on('admin_new_unread_message', handleNewMessage);
      socket.on('notifications_were_reset', handleResetNotifications);
      socket.on('admin_refresh_conversations', handleRefresh); // Bu, sohbet silindiğinde sayacı günceller.

      return () => {
        socket.off('admin_new_unread_message', handleNewMessage);
        socket.off('notifications_were_reset', handleResetNotifications);
        socket.off('admin_refresh_conversations', handleRefresh);
        if (socket.connected) socket.disconnect();
      };
    }
  }, [location.pathname]); // Sayfa değiştiğinde token'ı ve rolü yeniden kontrol et

  return (
    <Routes>
      <Route path="/" element={<LoginPage />} />
      <Route path="/forgot-password" element={<ForgotPasswordPage />} />
      <Route path="/home" element={<ProtectedRoute><HomePage /></ProtectedRoute>} />

      {/* Kullanıcılar için Yeni Rotalar */}
      <Route 
        path="/kredi-hesapla" 
        element={<ProtectedRoute><CreditCalculatorPage /></ProtectedRoute>} 
      />
      <Route 
        path="/piyasa-degeri" 
        element={<ProtectedRoute><MarketValuePage /></ProtectedRoute>} 
      />

      {/* Yöneticiye Özel Rotalar */}
      <Route 
        path="/dashboard" 
        element={<AdminProtectedRoute><Dashboard notificationCount={notificationCount} /></AdminProtectedRoute>} 
      />
      <Route path="/admin/vehicles" element={<AdminProtectedRoute><VehiclesPage /></AdminProtectedRoute>} />
      <Route path="/admin/personnel" element={<AdminProtectedRoute><PersonnelPage /></AdminProtectedRoute>} />
      <Route path="/admin/kredi" element={<AdminProtectedRoute><CreditCalculatorPage /></AdminProtectedRoute>} />
      <Route path="/admin/piyasa" element={<AdminProtectedRoute><MarketValuePage /></AdminProtectedRoute>} />
      <Route path="/admin/messages" element={<AdminProtectedRoute><MessagesPage /></AdminProtectedRoute>} />
    </Routes>
  );
}

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
