import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import './LoginPage.css';
import { socket } from './socket';

function LoginPage() {
  const [activeTab, setActiveTab] = useState('admin'); // 'admin', 'user', 'register'
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState(''); // Kayıt için
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  // LoginPage'e her gelindiğinde, önceden kalmış olabilecek
  // tüm aktif socket bağlantılarını kapat. Bu çok önemli!
  useEffect(() => {
    if (socket.connected) {
      socket.disconnect();
    }
  }, []);

  const handleTabClick = (tab) => {
    setActiveTab(tab);
    // Formları ve hataları temizle
    setEmail('');
    setPassword('');
    setName('');
    setError('');
    setMessage('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setMessage('');
    
    let url;
    let payload;
    let successAction;

    if (activeTab === 'register') {
      url = 'http://localhost:5000/api/register';
      payload = { name, email, password };
      successAction = () => {
        setMessage('Kayıt başarılı! Lütfen kullanıcı girişi yapın.');
        setTimeout(() => handleTabClick('user'), 2000);
      };
    } else { // 'admin' or 'user' login
      url = 'http://localhost:5000/api/login';
      payload = { email, password };
      successAction = (token) => {
        localStorage.setItem('token', token);
        const decoded = jwtDecode(token);
        // Kullanıcı rolüne göre doğru sayfaya yönlendir
        navigate(decoded.role === 'admin' ? '/dashboard' : '/home');
      };
    }

    try {
      const res = await axios.post(url, payload);
      if (activeTab === 'register') {
        successAction();
      } else {
        const decoded = jwtDecode(res.data.token);
        // Admin girişi sekmesindeyken rol kontrolü yap
        if (activeTab === 'admin' && decoded.role !== 'admin') {
          setError('Bu alan sadece yöneticilere aittir.');
          return;
        }
        successAction(res.data.token);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Bir hata oluştu.');
    }
  };

  return (
    <div className="login-container">
      <div className="login-wrapper">
        <div className="role-tabs">
          <button
            className={`tab-button ${activeTab === 'admin' ? 'active' : ''}`}
            onClick={() => handleTabClick('admin')}
          >
            Yönetici Girişi
          </button>
          <button
            className={`tab-button ${activeTab === 'user' ? 'active' : ''}`}
            onClick={() => handleTabClick('user')}
          >
            Kullanıcı Girişi
          </button>
          <button
            className={`tab-button ${activeTab === 'register' ? 'active' : ''}`}
            onClick={() => handleTabClick('register')}
          >
            Kayıt Ol
          </button>
        </div>

        <form className="login-form" onSubmit={handleSubmit}>
          <h2>
            {activeTab === 'admin' && 'Yönetici Girişi'}
            {activeTab === 'user' && 'Kullanıcı Girişi'}
            {activeTab === 'register' && 'Yeni Hesap Oluştur'}
          </h2>

          {activeTab === 'register' && (
            <input
              type="text"
              placeholder="Ad Soyad"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          )}

          <input
            type="email"
            placeholder="E-posta Adresi"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Şifre"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <button type="submit">
            {activeTab === 'register' ? 'Kaydol' : 'Giriş Yap'}
          </button>

          {error && <p className="error">{error}</p>}
          {message && <p className="success">{message}</p>}

          {activeTab !== 'register' && (
             <div className="form-switch">
                <button type="button" onClick={() => navigate('/forgot-password')}>
                  Şifremi Unuttum
                </button>
            </div>
          )}
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
