import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import AdminChatBox from './AdminChatBox';
import './MessagesPage.css';
import { useNavigate } from 'react-router-dom';
import { socket } from './socket';

function MessagesPage() {
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const fetchConversations = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Yetkilendirme token\'ƒ± bulunamadƒ±. L√ºtfen tekrar giri≈ü yapƒ±n.');
        setIsLoading(false);
        navigate('/');
        return;
      }
      const response = await axios.get('http://localhost:5000/api/conversations', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      setConversations(response.data);
      setError('');
    } catch (error) {
      console.error("Konu≈ümalar y√ºklenemedi:", error);
      const errorMessage = error.response?.data?.message || 'Konu≈ümalar y√ºklenirken bir hata olu≈ütu.';
      setError(errorMessage);
      if (error.response?.status === 403 || error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/');
      }
    } finally {
      setIsLoading(false);
    }
  }, [navigate]);

  // *** DEƒûƒ∞≈ûƒ∞KLƒ∞K: useEffect sadele≈ütirildi ***
  useEffect(() => {
    if (!socket.connected) {
      socket.connect();
    }
    
    // Bu sayfanƒ±n tek g√∂revi kendi verisini √ßekmektir.
    // Bildirim sƒ±fƒ±rlama sinyali g√∂ndermesine artƒ±k gerek yok, App.js bu i≈üi hallediyor.
    fetchConversations();

    const handleRefresh = () => fetchConversations();
    const handleConversationDeleted = (deletedConvId) => {
        if (selectedConversation?.conversation_id === deletedConvId) {
            setSelectedConversation(null);
        }
        fetchConversations();
    };

    socket.on('admin_refresh_conversations', handleRefresh);
    socket.on('conversation_deleted', handleConversationDeleted);

    return () => {
      socket.off('admin_refresh_conversations', handleRefresh);
      socket.off('conversation_deleted', handleConversationDeleted);
    };
  }, [fetchConversations, selectedConversation]);

  const handleDeleteConversation = async (e, conversationId) => {
    e.stopPropagation();
    if (window.confirm("Bu sohbeti ve i√ßindeki t√ºm mesajlarƒ± kalƒ±cƒ± olarak silmek istediƒüinizden emin misiniz?")) {
        try {
            const token = localStorage.getItem('token');
            await axios.delete(`http://localhost:5000/api/conversations/${conversationId}`, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
        } catch (error) {
            alert(error.response?.data?.message || "Sohbet silinirken bir hata olu≈ütu.");
        }
    }
  };

  const refreshConversations = () => {
    setIsLoading(true);
    fetchConversations();
  };

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Gelen Kutusu Y√ºkleniyor...</div>
      </div>
    );
  }

  if (error && conversations.length === 0) {
    return (
      <div className="error-container">
        <p>Hata: {error}</p>
        <button onClick={refreshConversations}>Tekrar Dene</button>
      </div>
    );
  }

  return (
    <div className="messages-page-container">
      <div className="conversations-sidebar">
        <div className="sidebar-header">
          <button onClick={() => navigate('/dashboard')} className="back-to-dashboard-btn" title="Y√∂netim Paneline D√∂n">
            ‚Äπ Panele D√∂n
          </button>
          <h2>Gelen Kutusu</h2>
          <button onClick={refreshConversations} className="refresh-btn" title="Yenile">
            üîÑ
          </button>
        </div>
        
        <div className="conversations-list">
          {conversations.length === 0 ? (
            <div className="no-conversations">
              <p>Hen√ºz bir g√∂r√º≈üme ba≈ülatƒ±lmamƒ±≈ü.</p>
            </div>
          ) : (
            conversations.map((convo) => (
              <div 
                key={convo.conversation_id} 
                className={`conversation-item ${
                  selectedConversation?.conversation_id === convo.conversation_id ? 'active' : ''
                }`}
                onClick={() => setSelectedConversation(convo)}
              >
                <div className="conversation-avatar">
                  {convo.user_name?.charAt(0).toUpperCase() || '?'}
                </div>
                
                <div className="conversation-content">
                  <div className="conversation-header">
                    <h4>{convo.user_name || 'Bilinmeyen Kullanƒ±cƒ±'}</h4>
                    <span className="conversation-time">
                      {new Date(convo.created_at).toLocaleString('tr-TR', {
                        hour: '2-digit', minute: '2-digit'
                      })}
                    </span>
                  </div>
                  
                  <div className="vehicle-info">
                    <span className="vehicle-name">{convo.brand} {convo.model}</span>
                  </div>
                  
                  <p className="last-message">{convo.message}</p>
                </div>
                <button 
                    className="delete-conversation-btn" 
                    title="Sohbeti Sil"
                    onClick={(e) => handleDeleteConversation(e, convo.conversation_id)}
                >
                    üóëÔ∏è
                </button>
              </div>
            ))
          )}
        </div>
      </div>

      <div className="chat-area">
        {selectedConversation ? (
          <AdminChatBox 
            key={selectedConversation.conversation_id}
            conversationId={selectedConversation.conversation_id}
          />
        ) : (
          <div className="no-chat-selected">
            <div className="no-chat-content">
              <h3>Ho≈ü Geldiniz!</h3>
              <p>Mesajlarƒ± g√∂r√ºnt√ºlemek i√ßin sol men√ºden bir konu≈üma se√ßin.</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default MessagesPage;
