import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';

// KORUYUCULAR
import ProtectedRoute from './ProtectedRoute'; 
import AdminProtectedRoute from './AdminProtectedRoute'; 

// SAYFALAR
import LoginPage from './LoginPage';
import ForgotPasswordPage from './ForgotPasswordPage';
import HomePage from './HomePage';           // Kullanıcının ilanları gördüğü sayfa
import Dashboard from './Dashboard';        // Adminin ana paneli
import VehiclesPage from './VehiclesPage';  // Adminin araçları yönettiği sayfa

function App() {
  return (
    <Router>
      <Routes>
        {/* Herkesin Erişebileceği Rotalar */}
        <Route path="/" element={<LoginPage />} />
        <Route path="/forgot-password" element={<ForgotPasswordPage />} />

        {/* ======================================= */}
        {/*      KULLANICIYA ÖZEL ROTA            */}
        {/* ======================================= */}
        {/* Normal kullanıcı giriş yapınca bu sayfayı görecek */}
        <Route 
          path="/home" 
          element={
            <ProtectedRoute>
              <HomePage />
            </ProtectedRoute>
          } 
        />

        {/* ======================================= */}
        {/*      YÖNETİCİYE ÖZEL ROTALAR            */}
        {/* ======================================= */}
        {/* Admin giriş yapınca bu sayfayı görecek */}
        <Route 
          path="/dashboard" 
          element={
            <AdminProtectedRoute>
              <Dashboard />
            </AdminProtectedRoute>
          } 
        />
        {/* Adminin diğer sayfaları */}
        <Route 
          path="/admin/vehicles" 
          element={
            <AdminProtectedRoute>
              <VehiclesPage />
            </AdminProtectedRoute>
          } 
        />

      </Routes>
    </Router>
  );
}

export default App;
