import React, { useState, useEffect, useRef } from 'react';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';
import './AdminChatBox.css';
import { socket } from './socket';

function AdminChatBox({ conversationId }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [adminUser, setAdminUser] = useState(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        setAdminUser(jwtDecode(token));
      } catch (error) { console.error("Admin token decode error:", error); }
    }
  }, []);

  useEffect(() => {
    if (!conversationId || !adminUser) return;
    
    socket.connect();

    const onConnect = () => socket.emit('join_room', conversationId);
    const handleLoadMessages = (loadedMessages) => setMessages(loadedMessages);
    const handleReceiveMessage = (message) => {
      if (message.conversation_id === conversationId) setMessages(prev => [...prev, message]);
    };
    const handleMessageDeleted = ({ messageId }) => setMessages(prev => prev.filter(msg => msg.id !== messageId));

    socket.on('connect', onConnect);
    socket.on('load_messages', handleLoadMessages);
    socket.on('receive_message', handleReceiveMessage);
    socket.on('message_deleted', handleMessageDeleted);

    if (socket.connected) onConnect();

    return () => {
      socket.off('connect', onConnect);
      socket.off('load_messages', handleLoadMessages);
      socket.off('receive_message', handleReceiveMessage);
      socket.off('message_deleted', handleMessageDeleted);
    };
  }, [conversationId, adminUser]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    const trimmedMessage = newMessage.trim();
    if (trimmedMessage === '' || !adminUser) return;
    
    const userIdPart = conversationId.split('-').find(p => p.startsWith('user_'));
    const vehicleIdPart = conversationId.split('-').find(p => p.startsWith('vehicle_'));
    if (!userIdPart) return;

    const receiver_id = parseInt(userIdPart.split('_')[1]);
    const vehicle_id = vehicleIdPart ? parseInt(vehicleIdPart.split('_')[1]) : null;

    socket.emit('send_message', {
      conversation_id: conversationId,
      sender_id: adminUser.id,
      receiver_id,
      vehicle_id,
      message: trimmedMessage,
    });
    setNewMessage('');
  };

  const handleDeleteMessage = async (messageId) => {
    if (window.confirm("Bu mesajı kalıcı olarak silmek istediğinizden emin misiniz?")) {
        try {
            const token = localStorage.getItem('token');
            await axios.delete(`http://localhost:5000/api/messages/${messageId}`, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
        } catch (error) {
            alert(error.response?.data?.message || "Mesaj silinirken bir hata oluştu.");
        }
    }
  };

  if (!conversationId) {
    return <div className="admin-chat-box-empty"><p>Görüntülemek için bir konuşma seçin.</p></div>;
  }

  return (
    <div className="admin-chat-box">
      <div className="chat-messages-admin">
        {messages.map((msg) => {
          const isAdminMessage = parseInt(msg.sender_id) === adminUser?.id;
          return (
            <div key={msg.id} className={`message-container ${isAdminMessage ? 'admin-message' : 'user-message'}`}>
              <div className={`message-bubble ${isAdminMessage ? 'admin-bubble' : 'user-bubble'}`}>
                <button 
                    className="delete-message-btn" 
                    onClick={() => handleDeleteMessage(msg.id)}
                    title="Mesajı Sil"
                >
                    ×
                </button>
                <p>{msg.message}</p>
                <span className="message-time">
                  {new Date(msg.created_at).toLocaleTimeString('tr-TR', { hour: '2-digit', minute: '2-digit' })}
                </span>
              </div>
            </div>
          );
        })}
        <div ref={messagesEndRef} />
      </div>
      
      <form className="chat-input-form-admin" onSubmit={handleSendMessage}>
        <input type="text" value={newMessage} onChange={(e) => setNewMessage(e.target.value)} placeholder="Cevabınızı yazın..." />
        <button type="submit" disabled={!newMessage.trim()}>➢</button>
      </form>
    </div>
  );
}

export default AdminChatBox;
