import React, { useState } from 'react';
import axios from 'axios';
import './LoginPage.css';

function LoginPage() {
  const [formType, setFormType] = useState('login'); // 'login' | 'register' | 'forgot'
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  const clearState = () => {
    setEmail('');
    setPassword('');
    setError('');
    setMessage('');
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/login', { email, password });
      localStorage.setItem('token', res.data.token);
      alert('Giriş başarılı!');
      window.location.href = '/dashboard';
    } catch (err) {
      setError(err.response?.data?.message || 'Giriş hatası');
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/register', { email, password });
      setMessage(res.data.message);
      clearState();
    } catch (err) {
      setError(err.response?.data?.message || 'Kayıt hatası');
    }
  };

  const handleForgotPassword = (e) => {
    e.preventDefault();
    setMessage("Bu demo uygulamasında şifre sıfırlama aktif değil.");
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={formType === 'login' ? handleLogin : formType === 'register' ? handleRegister : handleForgotPassword}>
        <h2>
          {formType === 'login'
            ? 'Giriş Yap'
            : formType === 'register'
            ? 'Kaydol'
            : 'Şifremi Unuttum'}
        </h2>

        <input
          type="email"
          placeholder="E-posta"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />

        {formType !== 'forgot' && (
          <input
            type="password"
            placeholder="Şifre"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        )}

        <button type="submit">
          {formType === 'login'
            ? 'Giriş Yap'
            : formType === 'register'
            ? 'Kaydol'
            : 'Şifremi Sıfırla'}
        </button>

        {error && <p className="error">{error}</p>}
        {message && <p className="success">{message}</p>}

        <div className="form-switch">
          {formType !== 'login' && (
            <button type="button" onClick={() => { setFormType('login'); clearState(); }}>
              Giriş Yap
            </button>
          )}
          {formType !== 'register' && (
            <button type="button" onClick={() => { setFormType('register'); clearState(); }}>
              Kaydol
            </button>
          )}
          {formType !== 'forgot' && (
            <button type="button" onClick={() => { setFormType('forgot'); clearState(); }}>
              Şifremi Unuttum
            </button>
          )}
        </div>
      </form>
    </div>
  );
}

export default LoginPage;
