import React from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

// Bu sayfaya özel CSS'i de import edeceğiz (bir sonraki adımda oluşturacağız)
import './UserDashboard.css';

function UserDashboard() {
  const navigate = useNavigate();
  let userEmail = '';

  // Token'ı al ve içinden email'i güvenli bir şekilde çıkar
  try {
    const token = localStorage.getItem('token');
    if (token) {
      const decodedToken = jwtDecode(token);
      userEmail = decodedToken.email;
    }
  } catch (error) {
    console.error("Token çözümlenemedi:", error);
    // Token geçersizse, kullanıcıyı çıkış yapmaya zorla
    navigate('/');
  }

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <div className="user-dashboard-container">
      <header className="user-dashboard-header">
        <div className="header-brand">
          Galerio
        </div>
        <div className="header-user-info">
          <span>Hoş geldin, {userEmail}</span>
          <button onClick={handleLogout} className="logout-btn">Çıkış Yap</button>
        </div>
      </header>

      <main className="user-dashboard-main">
        <div className="welcome-card">
          <h1>Kullanıcı Paneli</h1>
          <p>Araçlarınızı yönetebilir ve yeni ilanları keşfedebilirsiniz.</p>
          <button 
            className="my-vehicles-btn"
            onClick={() => navigate('/my-vehicles')}
          >
            Araçlarım
          </button>
        </div>
      </main>
    </div>
  );
}

export default UserDashboard;
